INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('87387cb1-291e-46c9-a41d-4fb8b07dcd26', 'Альпака', 'Млекопитающие', 'Парнокопытные', 'Альпака', 'Домашнее парнокопытное животное, произошедшее от викуньи (вигони). Разводят в высокогорном поясе Южной Америки (Анды). На сегодняшний день там обитает около трех миллионов альпака, большая часть из которых населяет Перу. Выращивают альпака для стрижки шерсти, из которой делают теплые и мягкие одеяла, пледы и одежду, а из меха делают предметы для дома.', 'https://cs6.livemaster.ru/storage/6f/c3/73524c4cdc57ced7138ce1f6f8gb.jpg', '2f3d00', 55.054682, 82.879244);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('86a4795e-5b1e-46b4-b3ac-75a3ca7ca44d', 'Черный волк', 'Млекопитающие', 'Хищные', 'Черный волк', 'Распространён в Северной Америке: Аляске, Канаде. Обитает в различных географических зонах от тундры и лесотундры до тайги и гор. Стая волков - это семья, обычно состоящая из 8-12 особей. Охотится, в основном, в сумерках и ночью. Питается копытными (сообща могут одолеть жертву в 10 раз больше своих размеров), а так же грызунами, птицами. Беременность 62 - 75 дней. Рождается 2 - 7 детёнышей. Внесён в Международную Красную книгу, в Приложение II Конвенции о Международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSHRYnUuODJnEwzgeRSb3ydPIEuDRz72l3sm1gNZ1-p2QG0Saq4Z1mTKmbMBA&s', '2f3d00', 55.055463, 82.877814);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('436ae634-fb3c-4075-a188-be62c539db7a', 'Волк полярный', 'Млекопитающие', 'Хищные', 'Волк полярный', 'Распространён на севере России, Кольском п-ове, Камчатке. Обитает в тундре и лесотундре. Стая волков - это семья, обычно состоящая из 8 - 12 особей. Охотится, в основном, в сумерках и ночью. Питается в основном копытными, а так же грызунами, птицами. Беременность 62 - 75 дней. Рождается 2 - 7 детёнышей. Внесён в Международную Красную книгу, в Приложение II Конвенции о Международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR7RLSGRDuziC8ZsYXUxC1vzQwV6Am9ugOIFq2jZfwpQ25NC5pz-1IUuwGAu8Y&s', '2f3d00', 55.055553, 82.878225);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('10af804d-ad6f-4830-819d-9b232e9a0973', 'Обыкновенный дикдик', 'Млекопитающие', 'Парнокопытные', 'Обыкновенный дикдик', 'Небольшая антилопа семейства полорогих, обитающая в Восточной Африке. Обыкновенный дикдик достигает высоты в холке до 40 см и весом 5,5 кг. Рога длиной 9,5 см. Это дневные животные, при высоких дневных температурах они отдыхают в тени и затем активны ночью. Для защиты от жары животное охлаждает кровь в слёзно-носовых протоках. Ограничивая приток крови к мышцам, за исключением участвующих в дыхании, дикдик может экономить энергию при дыхании, что выгодно при высоких температурах. Дикдики живут моногамно на постоянных территориях. Самец доминирует, защищая свою территорию от незваных гостей. Из-за незначительного размера дикдики являются лёгкой добычей для многих хищников.', 'https://i1.wp.com/zoopicture.ru/assets/2013/11/427.jpg', '2f3d00', 55.056065, 82.877443);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('fd632834-34b5-4d81-bfe8-03dda3046356', 'Белый медведь', 'Млекопитающие', 'Хищные', 'Белый медведь', 'Белый медведь - самый крупный из ныне живущих наземных хищников, единственный представитель существующих млекопитающих, весь жизненный цикл которых проходит на дрейфующих льдинах Арктики. Распространен на островах и побережьях Северного Ледовитого океана. Внесен в Красную книгу МСОП, Красную книгу РФ, в Приложение II Конвенции о международной торговле.', 'https://gas-kvas.com/grafic/uploads/posts/2023-09/1695889285_gas-kvas-com-p-kartinki-belii-medved-2.jpg', '2f3d00', 55.055878, 82.879398);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('8cf4acb4-e33f-46ad-941b-2182ceaf59e7', 'Овцебык', 'Млекопитающие', 'Парнокопытные', 'Овцебык', 'Овцебык - современник мамонта. В ледниковый период он был распространен по всей Европе, Америке и Сибири. В XX веке завезен на Аляску (где ранее вымер), в Евразию, на полуостров Таймыр, остров Врангеля, Норвегию. Распространен в Северной Америке: в арктических областях Гренландии и Канады. Внесен в Международную Красную книгу.', 'https://polinka.top/uploads/posts/2023-06/1686328968_polinka-top-p-ovtsebik-kartinki-pinterest-37.jpg', '2f3d00', 55.055604, 82.884084);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('5892abff-4dcc-4711-8276-ca4dc39dd6ad', 'Красный вари ', 'Млекопитающие', 'Приматы', 'Красный вари ', 'Лемур вари – наиболее крупный представитель семейства. Распространен на северо-восточном побережье острова Мадагаскар. Внесен в Международную Красную Книгу, в Приложение I Конвенции о международной торговле.', 'https://jooinn.com/images/red-lemur-3.jpg', '2f3d00', 55.05612, 82.887014);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('02eaa361-6ae2-4fd6-b652-d6bf2d958caf', 'Полосатый мангуст', 'Млекопитающие', 'Хищные', 'Полосатый мангуст', 'Полосатые мангусты – кочевники. Между собой общаются звуками, напоминающими щебетание. Известна невосприимчивость мангуст к яду змей, они ловко справляются даже с коброй. Мангустам угрожают хищные птицы, поэтому они постоянно начеку и, чем бы мангусты не занимались, один из них обязательно на страже. Увидев силуэт птицы, он криком предупреждает остальных об опасности.', 'https://pic.rutubelist.ru/video/2c/8a/2c8ae407039b0f7390f8b04f7100fd0f.jpg', '2f3d00', 55.055718, 82.886992);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('d9f889c7-99e3-4739-b649-030e904061c1', 'Уссурийский белогрудый медведь', 'Млекопитающие', 'Хищные', 'Уссурийский белогрудый медведь', ' спячку залегает в дуплах деревьев, в пещерах и расщелинах скал. Питается растительной пищей (желуди, кедровые орехи, трава, ягоды, плоды), насекомыми, дикими копытными. Беременность 180-240 дней. Рождается 1-3 детёныша.', 'https://osovet64.ru/wp-content/uploads/f/1/f/f1f6444dbc5083846c171cd759ed9cf3.jpeg', '2f3d00', 55.055337, 82.884856);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('6f06d1fe-6325-40c6-aadf-ee2d95959940', 'Уссурийская харза', 'Млекопитающие', 'Хищные', 'Уссурийская харза', 'Харза называется также пестрой, желтогрудой или дальневосточной куницей. Обитающая на Дальнем Востоке харза – самая крупная и наиболее яркая по расцветке меха среди сородичей, обитающих в Азии. Распространена в Амурском и Уссурийском краях, на северо-востоке Китая, Корейском полуострове. Внесена в Международную Красную книгу, в Красную книгу РФ, Приложение III Конвенции о международной торговле.', 'https://i.pinimg.com/originals/db/b1/a4/dbb1a47b40fefd9110672e2ab4cf670a.jpg', '2f3d00', 55.054509, 82.882661);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('9ce5fc16-a4a6-4ff2-878c-f5f2aa8e0ae7', 'Кенгуру Беннетта', 'Млекопитающие', 'Сумчатые', 'Кенгуру Беннетта', 'Видовое название дано в честь австралийского натуралиста Джорджа Беннетта. Распространен в Юго-Восточной Австралии, на о. Тасмания. Внесен в Международную Красную книгу.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSjMtVbeWs_dkNcZMxk-BK1FWKJKspscnhfXTx2yqzHbn3VwjV8IegNwFwwODM&s', '2f3d00', 55.05655, 82.88424);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('5af6ceb2-6e10-4d2b-bc11-080d74a8f178', 'Большой серый кенгуру', 'Млекопитающие', 'Сумчатые', 'Большой серый кенгуру', 'Обитает в Восточной и Юго-Западной Австралии. Также можно их встретить на о. Тасмании. Их обычный биотоп - это эвкалиптовая саванна. Встречаются также на покрытых густой растительностью равнинах и в открытых дождевых лесах.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRLWSZt5Q3m_93-sPZ4E06se9YIujgzwPup77RhJjwAUZ0ewi0S0w2-eq8tlw&s', '2f3d00', 55.056560000000005, 82.88425000000001);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('f8abb907-0706-439a-9ac4-07240cece776', 'Египетская летучая собака', 'Млекопитающие', 'Рукокрылые', 'Египетская летучая собака', 'Египетская летучая собака или нильский крылан. Область распространения простирается от Египта и Аравийского полуострова до юга Турции и Кипра. Животное часто заводят в качестве домашнего питомца, поскольку оно общительно и легко приручаемо. Продолжительность жизни до 30 лет', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTiV5EChzld488Jbwvm5YO6pEnzw0mCNkd-6PYDnHQZN5cskDKnsNxssX6GXtY&s', '2f3d00', 55.05657000000001, 82.88426000000001);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('c9135420-af93-4230-8def-17cee7d1dad0', 'Большая летучая лисица', 'Млекопитающие', 'Рукокрылые', 'Большая летучая лисица', 'игантская летучая лисица распространена в Индокитае, на полуострове Малакка от Южной Мьянмы, Таиланда, Малайзии, в Индонезии (Большие и Малые Зондские острова), на Филиппинах, на множестве мелких островов. Обитает в лесах. В горах встречается до 1300 м над уровнем моря.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQJZdYEtRrKF3nW1RXM4Mkx6rvaGNGwYoBrXOXEuq1xMlxZwFdNUEVkG9w1fzg&s', '2f3d00', 55.05658000000001, 82.88427000000001);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('a1ef5df2-b43e-4d1d-a968-7a5cb43372f1', 'Кошачий лемур', 'Млекопитающие', 'Приматы', 'Кошачий лемур', 'Кольцехвостый лемур, или кошачий лемур, или катта – единственный лемур, ведущий наземный образ жизни, предпочитая скальные участки. Встречается на юге и юго-западе острова Мадагаскар на сухих открытых пространствах и в лесах. Внесен в Красную книгу «Международного союза охраны природы».', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTw2m4gRICiiSJES9XrrNnyib7S82kQRePhwZRad4XzT3a3Tb_FY5ZzUa4KrQ&s', '2f3d00', 55.056590000000014, 82.88428000000002);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('41c0988b-80bb-43ca-98bb-95d4912e51e8', 'Черно-белый вари', 'Млекопитающие', 'Приматы', 'Черно-белый вари', 'Лемур вари – наиболее крупный представитель семейства. Внесен в Международную Красную книгу, в Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQWBCQX4c1_vbQ4UhOawfofC3yaVpQPKQ_KAQjqIGbS5tT1B6nsWDJPGpcQhhU&s', '2f3d00', 55.05661000000002, 82.88430000000002);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('45a646c8-8b03-4baa-9775-467b2416b23a', 'Толстохвостый галаго', 'Млекопитающие', 'Приматы', 'Толстохвостый галаго', 'Распространен в экваториальной и Юго-Восточной Африке. Крупнейшие популяции в Анголе, Танзании, на юге Кении и побережье Сомали. Обитает в прибрежных лесах, лесных массивах и кустарниковых зарослях, акациевых лесах.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSdNDLWAY4HYveYWPTJ6Qb4sK-o3FIELQ8KvZGrL5wrqNoL8A1NNakoA7gYE0k&s', '2f3d00', 55.056620000000024, 82.88431000000003);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('7f1e202a-39e4-48bd-9650-6cb5bb0b0268', 'Сенегальский галаго', 'Млекопитающие', 'Приматы', 'Сенегальский галаго', 'Сенегальский галаго широко распространён в Центральной Африке. Его область распространения простирается от Сенегала на западе до Судана, Сомали и Эфиопии на востоке и к югу до Кении и Танзании. Внесен в Международную Красную Книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ5CftKsDKt6zg2WMh-JfegFJFJSk9QCijM8z4WUwJkW967C1IJqQCuXrSzyw&s', '2f3d00', 55.05663000000003, 82.88432000000003);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('d66af5ac-d125-4618-8203-6b585bfd1f73', 'Толстый лори', 'Млекопитающие', 'Приматы', 'Толстый лори', 'аспространен от Восточной Индии до Вьетнама, на Малайском полуострове, в Западной Индонезии и на Филиппинах. Обитает в дождевых лесах на высоте до 1300 м над уровнем моря.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRKE7lExiCidEyXKxJRZJgg4zeMegZK2C1Btk0STrN2fA8oovvWXCFZzG4vXA&s', '2f3d00', 55.05664000000003, 82.88433000000003);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('ae36fbcd-18db-4930-80d7-db965664a51d', 'Малый Лори', 'Млекопитающие', 'Приматы', 'Малый Лори', 'Распространен на юге Китая, Вьетнаме, Лаосе, востоке Камбоджи. Внесен в  Международную Красную книгу, Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQyOd9x52RwSrrkvz7UMLlGBt5oo_5wVuCNnyXoZAiamg_YV2pd5pV_h4Cc5ro&s', '2f3d00', 55.05665000000003, 82.88434000000004);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('be9f221d-7628-4c06-a64f-d1ad1a3e7c4d', 'Обыкновенная игрунка', 'Млекопитающие', 'Приматы', 'Обыкновенная игрунка', 'Обыкновенная игрунка или уистити – очень симпатичная небольшая обезьянка. Обитают в северо-восточных и центральных лесах Бразилии. Внесена в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT4GcBsSKTMQ9ggUE0y_BB6VwumYVeFiWn6JEBDqWCS_dumKixtELGNmLHfrNs&s', '2f3d00', 55.056660000000036, 82.88435000000004);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('896d877c-a7c6-425d-abcd-66efb41ed243', 'Карликовая мармозетка', 'Млекопитающие', 'Приматы', 'Карликовая мармозетка', 'Карликовая мармозетка или игрунка карликовая является одним из наименьших представителей всего отряда приматов. Распространена в Южной Америке в верховьях реки Амазонки, на юге Колумбии, в Северо-Западной Бразилии, Восточном Эквадоре и Перу, Северной Боливии. Внесена в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSsLLRInlKK9xwCqKiFPldEhably46knoEW_foiPd-AYwBXxKCAvUR47HsoYA&s', '2f3d00', 55.05667000000004, 82.88436000000004);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('4422c689-8791-46d6-aaea-ea54aecd2481', 'Золотистый львиный тамарин', 'Млекопитающие', 'Приматы', 'Золотистый львиный тамарин', 'Распространен на очень ограниченной территории Атлантического побережья Бразилии, Рио-де-Жанейро. На этот вид ведется Международная племенная книга и все львиные тамарины, содержащиеся в неволе, являются собственностью программы сохранения вида и контролируются правительством Бразилии. Золотистый львиный тамарин один из самых редких видов животных, обитающих на нашей планете, и нуждающихся в сохранении и восстановлении. Внесен в Международную Красную книгу,в Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT7CoWtOGFNgCUCq-4d1sIFGgESDU2ptCpwVNFWZzmCg66RUrhKjt9-UpWjSw&s', '2f3d00', 55.05668000000004, 82.88437000000005);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('4e0810a8-91d0-438c-955e-b174115ba6ea', 'Эдипов тамарин', 'Млекопитающие', 'Приматы', 'Эдипов тамарин', 'битают исключительно в северо-западной Колумбии, в районе у побережья Карибского моря. Естественная среда обитания — это тропические дождевые и сухие леса, при этом животные предпочитают леса с густым подлеском.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRkVF-CdBA9_xwUNMOC3ppPpV-kNMsZHso-sWyWvlAhSTvNwqKb_7bFtScOCw&s', '2f3d00', 55.056690000000046, 82.88438000000005);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('6c66847d-15b8-49ee-8dcb-00db3d8ba6da', 'Гельдиевая мармозетка', 'Млекопитающие', 'Приматы', 'Гельдиевая мармозетка', 'Гельдиевая мармозетка или каллимико - видовой эпитет дан в честь швейцарского естествоиспытателя Эмиля Августа Гёльди (1859—1917). Распространена в Южной Америке, обитает в верховьях бассейна Амазонки в Боливии, Бразилии, Колумбии, Эквадоре и Перу. Находится в списке охраняемых видов, перемещение которых через границу Таможенного Союза ограничено.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ6FF4i5y63iZH-KgA0oBJWkDRyW1unf7paho8ZKp4t-ajt10ek6dukBepHWxw&s', '2f3d00', 55.05670000000005, 82.88439000000005);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('f0a5ac8c-b15b-4e2f-b66a-bb7f4f1e6568', 'Буроголовый тамарин', 'Млекопитающие', 'Приматы', 'Буроголовый тамарин', 'Буроголовый тамарин, или рыжеголовый тамарин обитает в Боливии, Бразилии, Колумбии, Эквадоре и Перу. Анатомическое строение тамаринов отличается от строения более крупных обезьян Нового Света. У них имеются видоизмененные когти - вместо ногтей - на всех пальцах, кроме большого, а также два коренных зуба - вместо трех - в каждой челюсти. Внесен в Международную Красную Книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQezF8wMqQWJcV647WGI0tAIz-3KAOW0IdHzzLwzPQ0QLRTs442dE1bT_Oo70c&s', '2f3d00', 55.05671000000005, 82.88440000000006);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('2b59b3a5-ab0b-425f-8aa7-9fbc75bf552d', 'Императорский тамарин', 'Млекопитающие', 'Приматы', 'Императорский тамарин', 'Название вида («императорский») связано с наличием у этих обезьян пышных белых «усов» и дано в честь кайзера Вильгельма II. Распространены в северо-западной Бразилии, восточном Перу и на севере Боливии. На востоке ареал ограничен рекой Гурупи, в верховьях Амазонки — реками Путумайо на севере и Мадейра на юге. Внесен в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS1VyFn9q8LpHtBf1hAUHgNfD4PIBfEm4M3XGh_yp0DiMItjpE_tVm4oxGJ3w&s', '2f3d00', 55.056720000000055, 82.88441000000006);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('eb7dbe11-6bb8-4b9c-80c1-2a4ff2f9c9ef', 'Игрунка Жоффруа', 'Млекопитающие', 'Приматы', 'Игрунка Жоффруа', 'Игрунка Жоффруа ранее считался подвидом обыкновенной игрунки, в 1988 году был поднят до ранга вида. ', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSo0etMNJAHHfa7sawx2r_rW9rV5P53hMzwTi0I-06gzvRohQnP1RpTTBKn3g&s', '2f3d00', 55.05673000000006, 82.88442000000006);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('85cfc0f9-2a1a-4156-aaa5-43cefb5e79f5', 'Краснорукий тамарин', 'Млекопитающие', 'Приматы', 'Краснорукий тамарин', 'Обитает в северо-восточной части Южной Америки, его ареал включает следующие страны: Гайана, Суринам и Французская Гвиана, а также северо-восток Бразилии к северу от Амазонки.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRis3oD25RlcdE7IVaHaYnjlFZX7zcyLSczHVZheqWHsNTHqvPwoxyY_-k0bQ&s', '2f3d00', 55.05674000000006, 82.88443000000007);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('0540cfbe-0dae-4927-bc04-c5bc97c52ff8', 'Белоплечий капуцин ', 'Млекопитающие', 'Приматы', 'Белоплечий капуцин ', 'Встречаются практически по всей Центральной Америке и на севере Южной Америки. В Центральной Америке ареал охватывает Гондурас, Никарагуа, Коста-Рику и Панаму. Также известны на востоке Гватемалы и юге Белиза. В Южной Америке встречаются на северо-востоке в Колумбии и в северо-западном Эквадоре.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSoG843eTe9NB4d8xzvWCctthpgrZYbbMHVmSEJBvOfQ0doG7TQ8TvkdaWeoA&s', '2f3d00', 55.056750000000065, 82.88444000000007);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('1c3ee729-da6f-496e-b39c-b93f005764bf', 'Бурый капуцин', 'Млекопитающие', 'Приматы', 'Бурый капуцин', 'Бурые капуцины одни из крупнейших представителей своего семейства. Распространены в Южной Америке: в Бразилии, Гайане, Суринаме, Венесуэле, Боливии и Французской Гвиане. Внесен в Международную Красную Книгу, в Приложение II о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQMdYFyumjFPqJZ6bTTEsjMFI85i1tLXAyWsi-VcXxnb3rDD4Xop3DlGgHPVg&s', '2f3d00', 55.05676000000007, 82.88445000000007);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('688a4b54-2082-4071-a81f-206816e29876', 'Беличий саймири', 'Млекопитающие', 'Приматы', 'Беличий саймири', 'Распространен на севере Южной Америки, от Колумбии и Гайаны, до Северной Бразилии и северо-востока Перу. Внесен в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT4hEbt_8kjNlV8pCR8u0dPMZsfbGHu_PWfqy1GWHpEL_i9hlcahLlbh6-UGw&s', '2f3d00', 55.05677000000007, 82.88446000000008);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('b7ab6079-35a6-4446-baa4-37ad1d34f274', 'Боливийский саймири	', 'Млекопитающие', 'Приматы', 'Боливийский саймири	', 'Обитают в Боливии, Бразилии и Перу в приречных лесах.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnJhG_lZKVnetyHczX25pCEXN-8JPJQNsgx2GptefYaoUw_RJv5atSO40L6js&s', '2f3d00', 55.056780000000074, 82.88447000000008);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('9848ba67-ee67-4f22-baf7-b61b6907a90e', 'Зеленая мартышка', 'Млекопитающие', 'Приматы', 'Зеленая мартышка', 'Распространена на востоке Африки, в Восточном Судане, Эритреи, Эфиопии. Продолжительность жизни в природе до 20 лет, в неволе — до 45 лет. Внесена в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTyK1E7L3pnsGPukQfz6HLMhqxmo4DrHgEANceWrDMqrAztOaT6dAw0NBnLlks&s', '2f3d00', 55.05679000000008, 82.88448000000008);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('b596c0ba-779b-4a0a-bae5-f1abd598686e', 'Мартышка Бразза', 'Млекопитающие', 'Приматы', 'Мартышка Бразза', 'Ведет древесный образ жизни. Держатся обычно небольшими семейными группами, но иногда собираются до 35 особей. Питается плодами деревьев, семенами, листьями, насекомыми. Беременность 180-213 дней. Рождается 1 детеныш.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRQ6d2DadNDyKE5GyWFEBpAJJUgy0I-zvVCJ97-6mQfbSsPbk8KasWqdnngtQ&s', '2f3d00', 55.05680000000008, 82.88449000000008);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('a0de15b3-872e-445f-9d29-59a27589cf86', 'Восточный колобус', 'Млекопитающие', 'Приматы', 'Восточный колобус', 'Восточный колобус или гвереца был обнаружен немецким натуралистом Эдуардом Рюппелем во время поездки в Абиссинию между 1830 и 1834 годом. «Колобус» в переводе с греческого означает «искалеченный» это название он получил в связи с отсутствием большого пальца кисти. Еще одной отличительной особенностью является сложное строение желудка, напоминающее таковое у жвачных животных.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTFB9PyKtJ04FL1ouKJPLuXjCJqEEyfFG7JaPHZ1UtkkGx8K3C7u7RbaSi-Ag&s', '2f3d00', 55.056810000000084, 82.88450000000009);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('1d5f9c22-998d-4cf3-a3ec-573d23c3c0f1', 'Мартышка Диана', 'Млекопитающие', 'Приматы', 'Мартышка Диана', 'Распространена в западной части Африки от юга Гвинеи и Сьерра Леона до юга Ганы. Внесена в Международную Красную книгу, в Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTJDqFPwW6st_j8Z8cHWouuKvrhSteYbcRhqNN04uajXVqLSJjt_Lf9DTEaVGc&s', '2f3d00', 55.05682000000009, 82.88451000000009);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('5f1400e3-0af4-466c-8488-a631ee2709ea', 'Мандрил', 'Млекопитающие', 'Приматы', 'Мандрил', 'Мандрил или сфинкс распространен на западе Центральной Африки, на юго-западе Камеруна, Габоне, Юго-Западном Конго. Внесен в Международную Красную книгу, в Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQpAwsWZu8rL--qALSGhkMu9_8cYB5SKI5iYeexPL2IN_zhUW7mKjXGBrQTbg&s', '2f3d00', 55.05683000000009, 82.8845200000001);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('a7d5e951-ed3c-4eb8-a2a1-9f7d4393c8ae', 'Яванский лангур', 'Млекопитающие', 'Приматы', 'Яванский лангур', 'Яванский лангур или Блестящий гульман распространен в Индонезии на островах Ява, Бали, Ломбок. Продолжительность жизни до 30 лет.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSQv_N4XZ66eSNyZ6iU9yYzeCZgiwvpaJh_yT3anlAsudXOqRjZ2xistN5yDQ&s', '2f3d00', 55.05684000000009, 82.8845300000001);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('bf528f79-c322-488c-9549-f2560cdfd703', 'Дымчатый очковый лангур', 'Млекопитающие', 'Приматы', 'Дымчатый очковый лангур', 'аспространён на Малаккском полуострове и на прилегающих островах, ареал простирается от южной Мьянмы и Таиланда до Малайзии. Средой обитания вида являются леса, при этом предпочтение отдаётся горным первичным и вторичным лесам.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRQJhw_bgEUeA49JfBU48xJCzaS65KQhY_XambbmEloGxMBndBDa7QCSLaf-g&s', '2f3d00', 55.0568500000001, 82.8845400000001);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('5cf81544-b4c6-40d1-9599-bcef7bc13365', 'Камбоджийский гиббон', 'Млекопитающие', 'Приматы', 'Камбоджийский гиббон', 'Камбоджийский гиббон или Кампучийский гиббон.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSMLibiqTnY5R08EjkE8o-1w6F8F_gqaKtARGtm7WtDb7yrKEY2RW0aEv1ZxcY&s', '2f3d00', 55.0568600000001, 82.8845500000001);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('a7d7c608-464b-415e-81f4-83cdb90aac2f', 'Борнейский орангутан', 'Млекопитающие', 'Приматы', 'Борнейский орангутан', 'Борнейский орангутан или Калимантанский орангутан один из двух ныне существующих видов орангутанов; обитает как на индонезийской, так и на малайзийской территориях острова. В самом названии орангутанов подчёркивается их близость к человеку. В переводе с малайского языка слово «орангутан» означает «лесной человек». Орангутаны живут дольше иных гоминидов, не считая человека. В неволе известны случаи, когда особи жили дольше 60 лет. Внесен в Международную Красную книгу, Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRMRUdb5hfQ5oRhrW1YmaEoIDdaVFxyZxMtMRW-9K2T5GiZOP4PwCD7d99pgg&s', '2f3d00', 55.0568700000001, 82.8845600000001);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('f89f1480-54a1-467f-ae64-74421f71b547', 'Двупалый ленивец', 'Млекопитающие', 'Неполнозубые', 'Двупалый ленивец', 'Двупалый ленивец или унау один из двух сохранившихся видов двупалых ленивцев распространен в Южной Америке, Северной Бразилии, на востоке Перу и Эквадора, Суринаме, Гайане, южной Венесуэле и Колумбии. Внесен в Международную Красную книгу.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTbuQDqX6ss7SN0dgMmcdMkBYZDTMeRlEJdYCqECHsmFDY8SnD7XYoz6we9I5M&s', '2f3d00', 55.056880000000106, 82.88457000000011);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('83c55d62-b5d3-46fb-a0aa-3ced7362949e', 'Заяц-беляк', 'Млекопитающие', 'Зайцеобразные', 'Заяц-беляк', 'битает в тундровой, лесной и частично лесостепной зоне Северной Европы (Скандинавия, северная Польша, изолированные популяции в Ирландии, Шотландии, Уэльсе), России, Казахстане, северо-западной Монголии, северо-восточного Китая, Японии (о. Хоккайдо). Акклиматизирован в Южной Америке (Чили и Аргентина). Населяет некоторые арктические острова (Новосибирские, Вайгач, Колгуев). В сравнительно недалеком прошлом был распространён значительно южнее; реликтовый участок прежнего ареала сохранился в Швейцарских Альпах.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSWB-k4srN412A9RU2d2XcohBPlppHQKWVYN7hykC8IiDxkreariQgudInvrZk&s', '2f3d00', 55.05689000000011, 82.88458000000011);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('722885e9-95e6-4478-a444-8e6d3415b030', 'Обыкновенная летяга', 'Млекопитающие', 'Грызуны', 'Обыкновенная летяга', ' ', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQaFvUcFAEhS6kJ_FtijdoshGe7vdnuUcOCV7DyD85F8uZUz-nCt5ujmvGOFQ&s', '2f3d00', 55.05690000000011, 82.88459000000012);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('e96cad4a-caa1-459b-b383-0125be9dc10b', 'Гигантская белка летяга', 'Млекопитающие', 'Грызуны', 'Гигантская белка летяга', 'Гигантские белки летяги — ночные животные, однако у наших посетителей есть все шансы их увидеть, по утрам эти белки довольно активны. В помёте рождается 1-2 детёныша. В рационе этих животных орехи, семена, ветки, фрукты.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTNtr3yhd4iV5aetX_kCsXgMXZDAJEIe6q2cx256Ct6EDQ74I2nDRbOaXQ-LTU&s', '2f3d00', 55.056910000000116, 82.88460000000012);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('2bcc7440-ea67-4fb3-b88e-391b2b96ae6e', 'Гребенчатый дикобраз', 'Млекопитающие', 'Грызуны', 'Гребенчатый дикобраз', 'Гребенчатый дикобраз или Хохлатый дикобраз - крупный грызун; второй по величине в фауне Старого Света после бобра и третий среди грызунов вообще после бобра и ещё более крупной южноамериканской капибары.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRMXWLQgqq3Z8vR1Ca_NcBoQnnMPxOzSE87ldVzSKQPvPJ8ccsETOPxLDZWPjM&s', '2f3d00', 55.05692000000012, 82.88461000000012);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('d7401ce9-e176-4730-87d3-597dcbae197c', 'Патагонская мара', 'Млекопитающие', 'Грызуны', 'Патагонская мара', 'Патагонская мара или патагонский заяц обитает в Южной Америке. Продолжительность жизни до 14 лет.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSBuNA6vphqDzVbZ2c8n0dd0b6W4xz31XWsb-YCVWKm8xyFR44juapkJ6mTlg&s', '2f3d00', 55.05693000000012, 82.88462000000013);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('c92931e5-2143-4e08-9684-a86acb95186d', 'Горная свинка Моко', 'Млекопитающие', 'Грызуны', 'Горная свинка Моко', 'битает на северо-востоке Бразилии в скалистых ландшафтах в штатах Минас-Жерайс, Баия и Пиауи.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTaSSKYwFUj1WlflO0_TKwNOv9Zey_P5P1l6oaqgGZ2WFWDHAhRbDFC7MYCqQ&s', '2f3d00', 55.056940000000125, 82.88463000000013);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('c1d0232a-056e-4994-be00-223b81151e13', 'Оливковый агути', 'Млекопитающие', 'Грызуны', 'Оливковый агути', 'и Азары населяет восточную Южную Америку. Они живут на востоке и юго-востоке Бразилии, в Парагвае и в северной Аргентин', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ1b8sQcHoRNCC1gTe__b2f2OqOsKLB-gF4mY5qdC3e350zJ19aRRhc1PG7yw&s', '2f3d00', 55.05695000000013, 82.88464000000013);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('2e27b88d-72f8-4776-9c41-895e70b0c13a', 'Пака', 'Млекопитающие', 'Грызуны', 'Пака', 'меет грубый мех без подшерстки, от темно-коричневого до черного на верхней части тела и белый или желтоватый на подбрюшье; на шерсти обычно имеет от трех до пяти рядов белых пятен вдоль его сторон. У него толстые сильные ноги, с четырьмя пальцами на передних лапах и пять на задних лапах (первый и пятый уменьшены).', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSAvj_6thADaMy5lBjhN7UO3kn6dYVnoArSwJh_5nYrL6XWMZY8nIifBXINxfY&s', '2f3d00', 55.05696000000013, 82.88465000000014);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('2866069d-29bb-428f-a17f-481eae4b674c', 'Капибара', 'Млекопитающие', 'Грызуны', 'Капибара', 'Капибара, или водосвинка - самый крупный среди современных грызунов. Распространена в Южной Америке в таких странах: Аргентина, Боливия, Бразилия, Венесуэла, Гайана, Колумбия, Парагвай, Перу, Уругвай, Французская Гвиана. Область распространения включает бассейны рек Ориноко, Амазонки и Ла-Платы. Внесена в Международную Красную книгу.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxHtTrl3pYroL51pJ8Y5YEC8SVPF0C__be4EDV7RvljH8gfLjowo3NS3n6aw&s', '2f3d00', 55.056970000000135, 82.88466000000014);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('b6a856a2-cc86-4213-b8db-5bfb9669a336', 'Серебристая нутрия', 'Млекопитающие', 'Грызуны', 'Серебристая нутрия', 'Нутрия, или коипу, или болотный бобр — единственный вид семейства нутриевых. Естественный ареал нутрии ограничен южной половиной Южной Америки от Боливии и южной Бразилии до Огненной Земли. Она была акклиматизирована во многих странах Европы и Северной Америки; в Африке не прижилась. В СССР акклиматизация нутрии удалась в Закавказье, Киргизии и Таджикистане.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRhltnszugpq30GgD8PzskBhuCg0Jaa956K-nrP6apMeFmpL-uggopr1KfRmB8&s', '2f3d00', 55.05698000000014, 82.88467000000014);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('cf5cb78f-6960-44f2-a130-7bac9364eddb', 'Дегу', 'Млекопитающие', 'Грызуны', 'Дегу', 'Белки дегу или чилийские белки – эти милые зверьки становятся популярными с каждым днём, и похоже совсем скоро они дадут фору декоративным кроликам, шиншиллам и хомячкам. Дегу – социальные животные, поэтому желательно содержать их группой. Поодиночке они зачастую чувствуют себя неуверенно и требуют повышенного внимания хозяина. Дегу может отбрасывать кончик своего хвоста, наподобие ящерицы. Эта удивительная способность часто помогает в естественных условиях обитания спасти зверьку жизнь. Место «отлома» хвоста довольно быстро подсыхает и заживает, не причиняя зверьку неудобств. Единственный минус — без хвоста дегу хуже держит равновесие. Поэтому не рекомендуется дергать животных за хвост.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSBSVb3w_2NNNkMbMbUxvV_zJXT-ZRn1sV2Hl5x8lZTNIJCOWOrfEbtmc0Efw&s', '2f3d00', 55.05699000000014, 82.88468000000015);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('7bbd9a97-8f20-466b-ba5d-01bf838afa27', 'Волк лесной', 'Млекопитающие', 'Хищные', 'Волк лесной', 'Распространен в Евразии, Северной Америке. Внесен в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ8875PdRiYhXKBW_Gmi4B5gkj--w_uo4edvusdooPGZ00BpD362Q0BqhZoGDY&s', '2f3d00', 55.057000000000144, 82.88469000000015);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('166b7e14-5f80-41fd-915c-21afd0542faa', 'Красный волк ', 'Млекопитающие', 'Хищные', 'Красный волк ', 'Распространен в Центральной и Южной Азии, о-вах Суматра и Ява, южных районах Дальнего Востока, Восточной Сибири. Внесен в Международную Красную книгу, Красную книгу РФ, в Приложение II Конвенции о международной торговле. Встречается крайне редко. На территории России в Приморском крае последний раз видели красного волка в 1972 году.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSdBG9L1iEeBI65-i4hC99XwHDK32Ym-MmfhPX770YkeXI1V_WZgjCY6MK4rBg&s', '2f3d00', 55.057030000000154, 82.88472000000016);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('82cdd5f9-c4be-4643-ae28-4baf21dc8f8c', 'Фенек', 'Млекопитающие', 'Хищные', 'Фенек', 'Фенек – самая маленькая африканская лисичка. В зоопарках мира этот вид представлен мало и в неволе размножается очень редко. Распространен в Северной Африке, на Синайском полуострове. Внесен в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTVfvRvMyRx-X8Ne48NDW6IUZER6mYhVrl2RQMPIWgjnILYbVyxMIFP5hx8Xw&s', '2f3d00', 55.05704000000016, 82.88473000000016);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('a8a5d2e6-aa22-4575-b958-4f347d69acf2', 'Корсак ', 'Млекопитающие', 'Хищные', 'Корсак ', 'Распространен в Казахстане, Средней Азии, на юге Западной Сибири, в Монголии и Северном Китае.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR5MLVfJg0iQS8x-KOzDRVnZSaay_MnrXtmZyV6Tt_ocwlSaLySMaxJlas-lQ&s', '2f3d00', 55.05705000000016, 82.88474000000016);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('68e64b98-d13d-41d7-9873-8dd592b61239', 'Лисица снежная', 'Млекопитающие', 'Хищные', 'Лисица снежная', ' ', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQHkY2VopwxYOv_6V8n8XUoFsWrX-l18jG2zi8LBfa_bLyOWxkaT0WbFMhbOk4&s', '2f3d00', 55.05706000000016, 82.88475000000017);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('4954f1af-8090-4c27-99b5-2bdc7fb42776', 'Обыкновенная лисица', 'Млекопитающие', 'Хищные', 'Обыкновенная лисица', ' ', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQjQVU66HMyBJBcI2Jg53OHuhDuciH0J_PoN5JtcC-ryXHrBy5wiiXct2fRYlk&s', '2f3d00', 55.05707000000017, 82.88476000000017);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('fd6e8b81-f903-4222-984c-f2f0cd4ae935', 'Бурый медведь', 'Млекопитающие', 'Хищные', 'Бурый медведь', 'Бурый медведь – второй по массивности хищник на планете. Распространен в Европе, Азии и Северной Америке от северной до южной границы лесов. Территория обитания огромна, но во многих районах он истреблен. В Альпах Австрии остались единицы, в горах Испании и Италии несколько десятков, в США сохранился только в пяти штатах. И лишь на Аляске, Камчатке и в Сибири численность еще довольна высока.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQColESiVcJa1pxI9xlE5K-HloxHDFGls9GqqVovBVJctABrBFgE5a1GdbYVg&s', '2f3d00', 55.05708000000017, 82.88477000000017);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('af36ea22-2c84-458d-9ead-155575fe42d6', 'Носуха ', 'Млекопитающие', 'Хищные', 'Носуха ', 'Распространена в Южной Америке от Колумбии и Венесуэлы до Северной Аргентины. Внесена в Международную Красную книгу, в Приложение III Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR92iPcJ45hv_PF9pTICGCyp7OxX4Di_9E2jFcWg632cAn1tEGDrGs12jKgdDI&s', '2f3d00', 55.05711000000018, 82.88480000000018);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('449c15c8-1698-4d25-a033-ed7691853147', 'Енот-полоскун', 'Млекопитающие', 'Хищные', 'Енот-полоскун', ' ', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR-4t6AHIcI1WG8Di0BUI0bS9B5qx4Kj89W_9r2SbvUo7usO4kSw-cNA4rbpA&s', '2f3d00', 55.05712000000018, 82.88481000000019);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('a3115c29-c81a-45f3-8bad-16156bb30a24', 'Кинкажу', 'Млекопитающие', 'Хищные', 'Кинкажу', 'Распространен в Южной Америке, от Мексики до Боливии. Внесен в Международную Красную книгу, в Приложение III Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTWAJVP3pdz8hlHT5IcgxLNey2iM7zqJH8NCEBE6w-K41Xx89i-c-2anmOe4Q&s', '2f3d00', 55.057130000000186, 82.88482000000019);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('8b07e7e3-d990-4c42-82ef-ee7604750f97', 'Росомаха', 'Млекопитающие', 'Хищные', 'Росомаха', 'Распространена на территории Скандинавских стран, Эстонии, России, на севере Монголии и Китая, Канаде, США. Внесена в Международную Красную книгу.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTWrlgxzouWdnjibjimTO6yLMlS6EUQG5PCRo_GHm5inXCCTVmZww9c73fMdkQ&s', '2f3d00', 55.05714000000019, 82.88483000000019);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('ef5e75a5-4563-4b5d-b052-2415728edcb3', 'Речная выдра', 'Млекопитающие', 'Хищные', 'Речная выдра', 'Распространена в Европе, Азии (кроме Аравийского полуострова и крайнего Севера), Северной Африке.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSTwSGiMQiotGFkIHo9ZezC_MZBKEn5PtSgF_kt6rdos2kFzE5pI1I6mLe8iyM&s', '2f3d00', 55.05715000000019, 82.8848400000002);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('aa519b78-e0a3-4df9-8f14-985fdb027869', 'Тайра', 'Млекопитающие', 'Хищные', 'Тайра', 'едет одиночный образ жизни. Активна в любое время суток. Наибольшую активность проявляет на рассвете и в сумерках. Хорошо лазает по деревьям, в случае необходимости может совершать большие прыжки. Питается фруктами, мелкими позвоночными, падалью. Беременность 63-67 дней. Рождается 1-3 детеныша.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSFxuWJ6xXWkK2qGIpx_VG69mgo5SZI6BmUZky2QfzLpnxwPwOjVvBdlTj9-jM&s', '2f3d00', 55.0571700000002, 82.8848600000002);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('9ad5cca0-1754-48a7-89a8-f321b7d6749e', 'Соболь', 'Млекопитающие', 'Хищные', 'Соболь', 'Соболь – ценный пушной вид, обитает только на территории России, экспорт которого запрещен. В настоящее время сохранился только в Сибири и на Дальнем Востоке. Издавна на Руси соболиный мех выступал в виде денежного средства, служил символом достатка. Соболь действительно был «меховым золотом» России. Его добывали везде, где он встречался, и к началу ХХ века численность зверей в России катастрофически сократилась. Живущий в основном в лесах таежной зоны, соболь исчез из европейской части России, да и в Сибири огромные пространства остались без соболей. Полный запрет отстрела зверей в 1912 году, организация Баргузинского заповедника, охрана и расселение соболей на прежние места обитания помогли спасти вид от полного истребления. Внесен в Международную Красную книгу.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT6VJ4xYxrUCiH7vHRdpBvmZf6cdSAFGMol9ExbHwn63C1gOEembwpBwHJDagE&s', '2f3d00', 55.0571800000002, 82.8848700000002);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('8e3a2d13-19f9-4f37-9f14-6f83cb61e157', 'Куница каменная', 'Млекопитающие', 'Хищные', 'Куница каменная', ' ', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTKIQM0yrTCP1-xG-Mct8juTsue3-1xRDAquk1aV7MsnqHTy3JmOcEJ-gOPhwU&s', '2f3d00', 55.057190000000205, 82.88488000000021);
