INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('da9e4598-3c1c-4c1d-b04f-e836817ca15e', 'Обыкновенный барсук', 'Млекопитающие', 'Хищные', 'Обыкновенный барсук', 'ивет небольшими семейными группами. В северных районах зимой впадает в спячку. Активен в сумерках и ночью. Питается в основном грызунами, земноводными, пресмыкающимися, моллюсками, орехами, червями, ягодами и насекомыми. Беременность около 280 дней. Рождается 2-6 детенышей.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxaUqKDvkmEJ4WUJvVX5j-QThV2L_V9QKluiQgGWzYKhJ8SyHRryWD8OrUIA&s', '2f3d00', 55.05721000000021, 82.88490000000021);
