INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('85d9e5d0-5bde-4530-bf86-3e828803165a', 'Норка американская', 'Млекопитающие', 'Хищные', 'Норка американская', 'Распространена в Северной Америке, акклиматизирована в Европе, России, Китае, Японии. В связи с расселением американской норки на территории России ареал европейской норки значительно сократился, а в некоторых районах европейская норка исчезла. Внесена в Международную Красную книгу.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRep6ox5ZHKIG-0mj-C3CYYg9aQMMeoaXzR-XEuGtweS0eZG-A_xnqP0qVLUUY&s', '2f3d00', 55.057290000000236, 82.88498000000024);
