INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('356c53d5-ea59-499e-9390-a674927c17c0', 'Суриката', 'Млекопитающие', 'Хищные', 'Суриката', 'Распространён на юге Африки. Внесён в Международную Красную Книгу.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ-7nagK8dGvZGHMQjBnHsoXHKMVEHOYdxfEHSGvdahSzyiqU9ZlCWqZ53Zvw&s', '2f3d00', 55.05736000000026, 82.88505000000026);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('18dc53b2-5b91-47b5-ac5b-7dfcaf037754', 'Бенгальская кошка', 'Млекопитающие', 'Хищные', 'Бенгальская кошка', ' ', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTnwHYXSSAsSJ-FcoLHcejZFcvUNF5pv5WwzmmAeHyimrQo92Ck4v0-S6O8OQ&s', '2f3d00', 55.05737000000026, 82.88506000000027);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('0525ae79-521e-4624-8790-5614ffe777f0', 'Каракал', 'Млекопитающие', 'Хищные', 'Каракал', 'ля убежищ использует норы лисиц, дикобразов. Питается грызунами, зайцами, мелкими антилопами, птицами, пресмыкающимися, насекомыми. Беременность 68-75 дней. Рождается 2-4 детёныша.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRRl_rrbvfeL0RU0aCDHZRfKAp4ammBJ-u3eLZJNkdTBSvBE9ex0dJ_olCpUw&s', '2f3d00', 55.057380000000265, 82.88507000000027);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('dae49b89-c062-46a7-8d12-ec27a661034c', 'Камышовый кот', 'Млекопитающие', 'Хищные', 'Камышовый кот', 'итается птицами, мелкими грызунами, рыбой, насекомыми, часто охотится на нутрий и водоплавающую птицу. Хорошо плавает. Беременность около 66 дней. Рождается 2-5 детенышей.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQlF0J1y6fRWhqpLJGOKEIJj_23NNrXCvxhNJLvBBD2H8yk9BI_afS7UFRBvgo&s', '2f3d00', 55.05739000000027, 82.88508000000027);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('95bf1545-2dcd-4f8a-8a5e-fd15eec50722', 'Манул', 'Млекопитающие', 'Хищные', 'Манул', 'Сибирский манул - самый северный подвид, который приспособился к резко континентальному климату с очень низкими температурами воздуха зимой (до -50°С) и снежным покровом незначительной величины. Осенью и в начале зимы манул бывает чрезвычайно жирен и малоподвижен. Высокий, рыхлый снежный покров затрудняет передвижение и добывание пищи, что не позволяет существовать этим кошкам в равнинных и горных лесах, где снега особенно много. Излюбленным местом обитания являются нагорья, холмистые пустыни, полупустыни и степи с выходами камней, расположенные, как правило, на склонах невысоких гор (до 1100-1500 м).', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTBIWZYBEWAFkbhSBxGEd39vIJTmExRk2mojlxLFQqy8-MttRXZP6vVhXGhzQ&s', '2f3d00', 55.05740000000027, 82.88509000000028);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('15e4a08a-b2ae-42d4-998b-26492157aedc', 'Дальневосточный кот', 'Млекопитающие', 'Хищные', 'Дальневосточный кот', 'Распространен от южной части Дальнего Востока до Северо-Восточного Китая, на Корейском п-ове. Внесен в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQqc7bTH_xk0sZ6DGULC0fmVN68zb_UOZDIIkA1vfkaBD4onj34k5IOZUtb4w&s', '2f3d00', 55.057410000000274, 82.88510000000028);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('f220f1cc-f92d-4bb5-9974-ceddf7769fbd', 'Сибирская рысь', 'Млекопитающие', 'Хищные', 'Сибирская рысь', 'Рысь – самый крупный и самый северный представитель мелких кошачьих. Распространена в Западной Сибири, на Алтае, в Хакасии. Обитает в густых хвойных и смешанных лесах, горной тайге Алтая. Внесена в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR3gNCLjETQmObSBujWAXkK5-hACYZFVg-vJcCTOumI9Aa5UvjxhEczU6cfeg&s', '2f3d00', 55.05742000000028, 82.88511000000028);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('f76f56df-bdfc-472c-a4e0-6bd74b330750', 'Восточно-сибирская рысь', 'Млекопитающие', 'Хищные', 'Восточно-сибирская рысь', ' ', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ-QX3ndJFbMqOzM1xjN11HSSxPxknLj9tD8BoZFLGbbjDDO_6dZUr1x_wJypM&s', '2f3d00', 55.05743000000028, 82.88512000000028);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('ee1ef289-788f-47de-8b15-117f2ad29704', 'Барханный кот', 'Млекопитающие', 'Хищные', 'Барханный кот', 'ктивен ночью и в сумерках. От высоких и низких температур укрывается в норах. Питается в основном мелкими грызунами, а также птицами, ящерицами, змеями, насекомыми и пауками. Одиночное животное. Беременность около 60-67 дней. Рождается 2-5 детенышей.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTSptz7iVWMt2tN9j1j9rI8lkj9rUWmrQUJ7QLMP2ZCUPpabNefHALR2TUc_w&s', '2f3d00', 55.057440000000284, 82.88513000000029);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('4988b325-06f3-4381-ae2f-e3d534af07bf', 'Оцелот', 'Млекопитающие', 'Хищные', 'Оцелот', 'Оцелот – малочисленный вид, который в ближайшее время может оказаться под угрозой исчезновения. Ставшие знаменитыми, из-за красоты своего меха, оцелоты были почти полностью уничтожены. Характер среды обитания и ночной образ жизни не позволяет вести их точный учет и изучать биологию. Распространен от южных границ США до Перу и Северной Аргентины.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQD4TBWc2VWA5Xy6udzvMYubpjcb9N2wm9mWtft52LcHqKfgd_sX_ieykkVm2Q&s', '2f3d00', 55.05745000000029, 82.88514000000029);
