INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('51723107-262f-4f50-88f2-e5f9547a97f0', 'Виверровая кошка', 'Млекопитающие', 'Хищные', 'Виверровая кошка', 'ктивна ночью и в сумерках. Виверровая кошка или кошка – рыболов, единственный представитель семейства кошачьих, который хорошо плавает и ныряет. Питается рыбой, ракообразными, а также птицами и млекопитающими. Беременность 57-67 дней. Рождается 2-3 детеныша.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSzh_I4zBcSFp7rjRMBL0PbZBFfXkeqLqsb9K_fpVEOeaqitZT_rhCIZAF6nlo&s', '2f3d00', 55.0575000000003, 82.8851900000003);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('f828b34d-68c3-4710-b146-068136e4aa4b', 'Ягуарунди', 'Млекопитающие', 'Хищные', 'Ягуарунди', 'едет наземный образ жизни, но и хорошо лазает по деревьям. Активна обычно ночью. Питается мелкими млекопитающими, птицами, рептилиями. Одиночное животное. Беременность 63-70 дней. Рождается 2-4 детеныша.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSkSEvY0t5P2fkS0EGF6FU_dLoEpBB4j-alr0Sa8kkNovui9tk02RMzLezsfA&s', '2f3d00', 55.057510000000306, 82.88520000000031);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('b5b9c293-2084-4b06-b0c3-d9ebad5da62d', 'Дымчатый леопард', 'Млекопитающие', 'Хищные', 'Дымчатый леопард', ' ', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQW43ZUnq96EcL28R2XSjaAp8T0g3hGrGN6l1RCEU2mXkZ4TggBdGIJ8pLh2fI&s', '2f3d00', 55.05752000000031, 82.88521000000031);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('3029b31a-3cfa-4a82-a643-69549cf52d94', 'Африканский лев', 'Млекопитающие', 'Хищные', 'Африканский лев', 'оловой диморфизм очень резко выражен, густая и длинная грива самцов придает им весьма своеобразный внешний вид.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRP0UxXF9yw0F3XtbBGcOsi8gUyIYIaycB2Gh-8DVlanWauz5pH6nWWRN0asQ&s', '2f3d00', 55.05753000000031, 82.88522000000032);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('ed6416f6-f3e6-46e5-9cca-c0c3845f4da4', 'Дальневосточный леопард', 'Млекопитающие', 'Хищные', 'Дальневосточный леопард', 'Питается копытными, зайцами и птицами. Свою добычу часто затаскивает на дерево. Ведет одиночный образ жизни. Беременность 90-100 дней. Рождается 1-4 детеныша.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTw4kQwSTxgSYFmD_XG3k4DguVYYv8n8fi3kT_a-DIRn6sPmpWfWHwFBidwrw&s', '2f3d00', 55.057540000000316, 82.88523000000032);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('fb8be392-278c-47f4-a4e5-30a9090cea5c', 'Персидский леопард ', 'Млекопитающие', 'Хищные', 'Персидский леопард ', 'хотится, в основном, в сумерках и ночью. Питается копытными, грызунами, птицами, пресмыкающимися. Беременность 90-105 дней. Рождается 1-4 детеныша.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTQ7ZJmO7Gd6acnmKBcGXbp3ttTuHKV9syyiiygz_CKP5aZCOiS__t1TgQ5GEY&s', '2f3d00', 55.05755000000032, 82.88524000000032);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('9022b972-5a46-4068-85ec-81f803e5ab35', 'Кавказский леопард', 'Млекопитающие', 'Хищные', 'Кавказский леопард', 'Распространен на Большом Кавказе, в Закавказье. В природе практически исчез. Внесен в Международную Красную книгу, Красную книгу РФ, в Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR0DnFMtbltQ9MaTaG1bl3HBBTfFa323E1ZsOwxS30tPeBDbhz3JE6GUSCkUQ&s', '2f3d00', 55.05756000000032, 82.88525000000033);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('c67e3c61-8519-475e-a62a-e38a599dac36', 'Амурский тигр ', 'Млекопитающие', 'Хищные', 'Амурский тигр ', 'Распространен на Дальнем Востоке, в Уссурийском и Приморском краях. В природе, благодаря природоохранным мероприятиям, численность возросла с 220 до 350 особей. В настоящее время в 289 зоопарках мира содержится 937 амурских тигров. Внесен в Международную Красную книгу, Красную книгу РФ, в Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSBv0fVyusqk3dKTAnsGq6v0R6behmPCV4d49UBSvGka4FTErpE1cYRycsVLDw&s', '2f3d00', 55.057570000000325, 82.88526000000033);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('e822860f-7a26-455a-aa90-86304f4e54fa', 'Бенгальский тигр белая вариация', 'Млекопитающие', 'Хищные', 'Бенгальский тигр белая вариация', 'ерсть светло-кремового цвета с серо-коричневыми полосами. Глаза голубые с розовым зрачком. Это – результат нарушения окраски кожных покровов и радужной оболочки глаз, обусловленный генетическими механизмами.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRHV_RGyrmdUcjrYHI6WrOZZCTZwgq77jajtuZm-LW8gjcHkPPb-5f8GIeroXU&s', '2f3d00', 55.05758000000033, 82.88527000000033);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('824fdf3a-255e-4f68-9e37-5acc0be53e37', 'Гепард', 'Млекопитающие', 'Хищные', 'Гепард', 'Распространен в Африке и Азии. Внесён в Международную Красную книгу, в Приложение I Конвенции о международной торговле. Гепард отличается мирным нравом, сравнительно легко приручается и сильно привязывается к человеку. С давних времен использовался в качестве ловчего зверя. В Египте еще недавно выполнял роль сторожевой собаки. Первые сведения о приручении и использовании гепарда на охоте в Египте и Азии (Палестина, Иордания, Сирия, Ирак, Афганистан, Пакистан, Индия, Китай) датируются вторым тысячелетием до н.э. С Х-ХI века охота с гепардом была очень популярна на юге Европы, в средние века в Древней Руси также практиковалась охота с гепардами.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTo817dWEUCBaMrg7jDfFaOWr1LwyPCVWxagYkLBNS8NvIYvKDdbSQE9t4dIw&s', '2f3d00', 55.05759000000033, 82.88528000000034);
