INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('b328eaf7-e892-496b-973f-a23749999c43', 'Лама', 'Млекопитающие', 'Парнокопытные', 'Лама', 'Распространена в центральной части южноамериканских Анд, от юга Перу до юга Аргентины. Одомашнена 2 000-2 500 лет до н.э. Используется как вьючное животное. Внесена в Международную Красную книгу, в приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQwgRwB9aYVWX1DcTCXpOzvOCtsQ7GDg4vnH-bXnWL_c0HMFOJ3GbQqFl1ErXw&s', '2f3d00', 55.05783000000041, 82.88552000000041);
