INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('15a9716b-a426-4d3e-97eb-783bce378d2b', 'Сибирская косуля', 'Млекопитающие', 'Парнокопытные', 'Сибирская косуля', 'ержится небольшими стадами. Питается почками, побегами и корой деревьев, травянистыми растениями, ягодами, грибами. Беременность около 9 месяцев. Рождается 1-3 детеныша. Летом косули держатся поодиночке, но встречаются группы по 2-4 особи. К осени сбиваются в стада до 20-30 животных. Рождается обычно 2, редко 3 малыша весом по 1,5-2 кг. На отдых косули укладывают своих детенышей на значительном расстоянии друг от друга. Это своеобразная стратегия защиты малышей от хищников: даже если хищник обнаруживает одного, второй остается незамеченным.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQMWldog3y3XCGrExk6k1sf_k6uiAuHYZa3Sr6GDJudvVa7eXZuyW8EfUD0mQ&s', '2f3d00', 55.05786000000042, 82.88555000000042);
