INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('9e5387f1-50bb-4717-8756-742a340b6950', 'Лось', 'Млекопитающие', 'Парнокопытные', 'Лось', 'шо плавает, ныряет. Держится поодиночке или группами по 3-5 особей. Питается травянистыми растениями, особенно водными и полуводными, хвоей, корой, веточками и листьями деревьев и кустарников, грибами. Беременность 7,5-8 месяцев. В апреле-мае рождается 1-2 лосенк', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTYbsGLasgfWhqP_hNwpVtgWE0pNXS2VvnBBB61N_rqEgbM4zrRwq4NLCWgZBQ&s', '2f3d00', 55.05791000000043, 82.88560000000044);
