INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('afbe48c7-934e-4b02-9315-67db73867ec3', 'Такин', 'Млекопитающие', 'Парнокопытные', 'Такин', 'тадное полигамное животное. Основная пища — трава и листья. Такин является активным дневным животным. Такины собираются в маленькие группы летом и собираются в стада до ста особей зимой, старые самцы ведут уединённый образ жизни.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSbuMgFQlScVuna_s8DJz8DPPNqzefZk4gzj3bCwDf184SYWgMU5Xl76zZv6w&s', '2f3d00', 55.05802000000047, 82.88571000000047);
