INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('100c467a-411d-46d7-b43f-ba09e8ee9a1d', 'Кенгуру Беннетта', 'Млекопитающие', 'Сумчатые', 'Кенгуру Беннетта', 'Видовое название дано в честь австралийского натуралиста Джорджа Беннетта. Распространен в Юго-Восточной Австралии, на о. Тасмания. Внесен в Международную Красную книгу.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRdEhDnIQeqpDphtScdr6UqT5TbTGhbwI2vBJhNXGRJOckucpfLzkKlpQWxXg&s', '2f3d00', 55.05349261985931, 82.87835848132285);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('23ec11ca-c76c-4d27-8f62-5679a4249150', 'Большой серый кенгуру', 'Млекопитающие', 'Сумчатые', 'Большой серый кенгуру', 'Обитает в Восточной и Юго-Западной Австралии. Также можно их встретить на о. Тасмании. Их обычный биотоп - это эвкалиптовая саванна. Встречаются также на покрытых густой растительностью равнинах и в открытых дождевых лесах.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRLWSZt5Q3m_93-sPZ4E06se9YIujgzwPup77RhJjwAUZ0ewi0S0w2-eq8tlw&s', '2f3d00', 55.05751932120553, 82.87411113909081);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('ed00c714-a2b4-41a8-bf16-c47233959f6c', 'Египетская летучая собака', 'Млекопитающие', 'Рукокрылые', 'Египетская летучая собака', 'Египетская летучая собака или нильский крылан. Область распространения простирается от Египта и Аравийского полуострова до юга Турции и Кипра. Животное часто заводят в качестве домашнего питомца, поскольку оно общительно и легко приручаемо. Продолжительность жизни до 30 лет', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTiV5EChzld488Jbwvm5YO6pEnzw0mCNkd-6PYDnHQZN5cskDKnsNxssX6GXtY&s', '2f3d00', 55.055986021895244, 82.88308971662417);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('782a8005-de8d-49aa-8771-c8c4d9409fcd', 'Большая летучая лисица', 'Млекопитающие', 'Рукокрылые', 'Большая летучая лисица', 'игантская летучая лисица распространена в Индокитае, на полуострове Малакка от Южной Мьянмы, Таиланда, Малайзии, в Индонезии (Большие и Малые Зондские острова), на Филиппинах, на множестве мелких островов. Обитает в лесах. В горах встречается до 1300 м над уровнем моря.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQJZdYEtRrKF3nW1RXM4Mkx6rvaGNGwYoBrXOXEuq1xMlxZwFdNUEVkG9w1fzg&s', '2f3d00', 55.05400294680094, 82.8763221063494);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('6272cb1b-d2c3-4818-9552-1c40b4f2af88', 'Кошачий лемур', 'Млекопитающие', 'Приматы', 'Кошачий лемур', 'Кольцехвостый лемур, или кошачий лемур, или катта – единственный лемур, ведущий наземный образ жизни, предпочитая скальные участки. Встречается на юге и юго-западе острова Мадагаскар на сухих открытых пространствах и в лесах. Внесен в Красную книгу «Международного союза охраны природы».', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTw2m4gRICiiSJES9XrrNnyib7S82kQRePhwZRad4XzT3a3Tb_FY5ZzUa4KrQ&s', '2f3d00', 55.05654780075879, 82.88277085017238);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('529c12eb-596c-4cb3-8de2-8c1d59f6c922', 'Красный вари ', 'Млекопитающие', 'Приматы', 'Красный вари ', 'Лемур вари – наиболее крупный представитель семейства. Распространен на северо-восточном побережье острова Мадагаскар. Внесен в Международную Красную Книгу, в Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQD4MjkX3UDPE8RjaifF_mFX-trCPd1ZiwwExlOg2rFz4_g6D82ZQJC3Iot6g&s', '2f3d00', 55.05353428244016, 82.87534615983236);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('32087ea1-015e-4bc0-be5b-f9f8e3e7f2ea', 'Черно-белый вари', 'Млекопитающие', 'Приматы', 'Черно-белый вари', 'Лемур вари – наиболее крупный представитель семейства. Внесен в Международную Красную книгу, в Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQWBCQX4c1_vbQ4UhOawfofC3yaVpQPKQ_KAQjqIGbS5tT1B6nsWDJPGpcQhhU&s', '2f3d00', 55.057812900343855, 82.87658596314711);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('a2e95575-4e35-4ce3-bf50-bf8d2b5b415f', 'Толстохвостый галаго', 'Млекопитающие', 'Приматы', 'Толстохвостый галаго', 'Распространен в экваториальной и Юго-Восточной Африке. Крупнейшие популяции в Анголе, Танзании, на юге Кении и побережье Сомали. Обитает в прибрежных лесах, лесных массивах и кустарниковых зарослях, акациевых лесах.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSdNDLWAY4HYveYWPTJ6Qb4sK-o3FIELQ8KvZGrL5wrqNoL8A1NNakoA7gYE0k&s', '2f3d00', 55.05465615073565, 82.87790640760663);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('a6c81766-519c-4cd9-83de-4b01b978be83', 'Сенегальский галаго', 'Млекопитающие', 'Приматы', 'Сенегальский галаго', 'Сенегальский галаго широко распространён в Центральной Африке. Его область распространения простирается от Сенегала на западе до Судана, Сомали и Эфиопии на востоке и к югу до Кении и Танзании. Внесен в Международную Красную Книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ5CftKsDKt6zg2WMh-JfegFJFJSk9QCijM8z4WUwJkW967C1IJqQCuXrSzyw&s', '2f3d00', 55.057937530737554, 82.88251212641431);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('8a28f573-88e8-4050-be8e-047e34b99bfc', 'Толстый лори', 'Млекопитающие', 'Приматы', 'Толстый лори', 'аспространен от Восточной Индии до Вьетнама, на Малайском полуострове, в Западной Индонезии и на Филиппинах. Обитает в дождевых лесах на высоте до 1300 м над уровнем моря.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRKE7lExiCidEyXKxJRZJgg4zeMegZK2C1Btk0STrN2fA8oovvWXCFZzG4vXA&s', '2f3d00', 55.05641360967342, 82.87949504596443);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('f19f4b9a-7484-40b3-a83b-9295d3a2d017', 'Малый Лори', 'Млекопитающие', 'Приматы', 'Малый Лори', 'Распространен на юге Китая, Вьетнаме, Лаосе, востоке Камбоджи. Внесен в  Международную Красную книгу, Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQyOd9x52RwSrrkvz7UMLlGBt5oo_5wVuCNnyXoZAiamg_YV2pd5pV_h4Cc5ro&s', '2f3d00', 55.05710064878008, 82.87672449475531);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('d959e7d2-e9d7-4b99-a208-cb98cc001edc', 'Обыкновенная игрунка', 'Млекопитающие', 'Приматы', 'Обыкновенная игрунка', 'Обыкновенная игрунка или уистити – очень симпатичная небольшая обезьянка. Обитают в северо-восточных и центральных лесах Бразилии. Внесена в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT4GcBsSKTMQ9ggUE0y_BB6VwumYVeFiWn6JEBDqWCS_dumKixtELGNmLHfrNs&s', '2f3d00', 55.0569718889856, 82.88090556479071);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('8cf2c252-9c8c-442b-9a1b-235b5a260206', 'Карликовая мармозетка', 'Млекопитающие', 'Приматы', 'Карликовая мармозетка', 'Карликовая мармозетка или игрунка карликовая является одним из наименьших представителей всего отряда приматов. Распространена в Южной Америке в верховьях реки Амазонки, на юге Колумбии, в Северо-Западной Бразилии, Восточном Эквадоре и Перу, Северной Боливии. Внесена в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSsLLRInlKK9xwCqKiFPldEhably46knoEW_foiPd-AYwBXxKCAvUR47HsoYA&s', '2f3d00', 55.05772608332833, 82.88511646000785);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('841010b6-38f8-42fe-8c0d-4b2aef9e8f42', 'Золотистый львиный тамарин', 'Млекопитающие', 'Приматы', 'Золотистый львиный тамарин', 'Распространен на очень ограниченной территории Атлантического побережья Бразилии, Рио-де-Жанейро. На этот вид ведется Международная племенная книга и все львиные тамарины, содержащиеся в неволе, являются собственностью программы сохранения вида и контролируются правительством Бразилии. Золотистый львиный тамарин один из самых редких видов животных, обитающих на нашей планете, и нуждающихся в сохранении и восстановлении. Внесен в Международную Красную книгу,в Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT7CoWtOGFNgCUCq-4d1sIFGgESDU2ptCpwVNFWZzmCg66RUrhKjt9-UpWjSw&s', '2f3d00', 55.05767081955298, 82.87715558313006);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('c4cc1c66-b512-4da2-bc49-c25a9ea4599f', 'Эдипов тамарин', 'Млекопитающие', 'Приматы', 'Эдипов тамарин', 'битают исключительно в северо-западной Колумбии, в районе у побережья Карибского моря. Естественная среда обитания — это тропические дождевые и сухие леса, при этом животные предпочитают леса с густым подлеском.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRkVF-CdBA9_xwUNMOC3ppPpV-kNMsZHso-sWyWvlAhSTvNwqKb_7bFtScOCw&s', '2f3d00', 55.0540839120504, 82.87719758440876);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('9ad1efaf-1816-49bc-a3ae-7bab8b194974', 'Гельдиевая мармозетка', 'Млекопитающие', 'Приматы', 'Гельдиевая мармозетка', 'Гельдиевая мармозетка или каллимико - видовой эпитет дан в честь швейцарского естествоиспытателя Эмиля Августа Гёльди (1859—1917). Распространена в Южной Америке, обитает в верховьях бассейна Амазонки в Боливии, Бразилии, Колумбии, Эквадоре и Перу. Находится в списке охраняемых видов, перемещение которых через границу Таможенного Союза ограничено.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ6FF4i5y63iZH-KgA0oBJWkDRyW1unf7paho8ZKp4t-ajt10ek6dukBepHWxw&s', '2f3d00', 55.05552500738788, 82.87609563739166);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('7d37488b-15d6-4278-9891-8f6393c2193c', 'Буроголовый тамарин', 'Млекопитающие', 'Приматы', 'Буроголовый тамарин', 'Буроголовый тамарин, или рыжеголовый тамарин обитает в Боливии, Бразилии, Колумбии, Эквадоре и Перу. Анатомическое строение тамаринов отличается от строения более крупных обезьян Нового Света. У них имеются видоизмененные когти - вместо ногтей - на всех пальцах, кроме большого, а также два коренных зуба - вместо трех - в каждой челюсти. Внесен в Международную Красную Книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQezF8wMqQWJcV647WGI0tAIz-3KAOW0IdHzzLwzPQ0QLRTs442dE1bT_Oo70c&s', '2f3d00', 55.05493206058171, 82.87960507400187);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('e165fd99-de1f-4ce5-b02b-48c4cc080878', 'Императорский тамарин', 'Млекопитающие', 'Приматы', 'Императорский тамарин', 'Название вида («императорский») связано с наличием у этих обезьян пышных белых «усов» и дано в честь кайзера Вильгельма II. Распространены в северо-западной Бразилии, восточном Перу и на севере Боливии. На востоке ареал ограничен рекой Гурупи, в верховьях Амазонки — реками Путумайо на севере и Мадейра на юге. Внесен в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS1VyFn9q8LpHtBf1hAUHgNfD4PIBfEm4M3XGh_yp0DiMItjpE_tVm4oxGJ3w&s', '2f3d00', 55.05566338089169, 82.87538815371913);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('85360c99-54ba-4620-b0cd-2db91d6d0a02', 'Игрунка Жоффруа', 'Млекопитающие', 'Приматы', 'Игрунка Жоффруа', 'Игрунка Жоффруа ранее считался подвидом обыкновенной игрунки, в 1988 году был поднят до ранга вида. ', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSo0etMNJAHHfa7sawx2r_rW9rV5P53hMzwTi0I-06gzvRohQnP1RpTTBKn3g&s', '2f3d00', 55.05713959838722, 82.88228617240449);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('cb6a56dd-1574-40a9-af5d-837b61fa2433', 'Краснорукий тамарин', 'Млекопитающие', 'Приматы', 'Краснорукий тамарин', 'Обитает в северо-восточной части Южной Америки, его ареал включает следующие страны: Гайана, Суринам и Французская Гвиана, а также северо-восток Бразилии к северу от Амазонки.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSJVq2DG2ORCm1DonfLLxrGs_9vfclDW-XLp6IPG8ct3OK4_eYFe6QmpN5YN24&s', '2f3d00', 55.056704390388056, 82.88546842596315);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('b4681c5c-5a86-47a1-bec7-85c3cfae7e9e', 'Белоплечий капуцин ', 'Млекопитающие', 'Приматы', 'Белоплечий капуцин ', 'Встречаются практически по всей Центральной Америке и на севере Южной Америки. В Центральной Америке ареал охватывает Гондурас, Никарагуа, Коста-Рику и Панаму. Также известны на востоке Гватемалы и юге Белиза. В Южной Америке встречаются на северо-востоке в Колумбии и в северо-западном Эквадоре.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRRj7slIeU-CfzUHwgdsQcEs8hoztI6VpBV5zjkejdtnuQkDsye4e-zVnWr4Xs&s', '2f3d00', 55.05466962466952, 82.88277661821311);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('95c673d2-9fca-4bc0-9bd3-2b7084ba68e5', 'Бурый капуцин', 'Млекопитающие', 'Приматы', 'Бурый капуцин', 'Бурые капуцины одни из крупнейших представителей своего семейства. Распространены в Южной Америке: в Бразилии, Гайане, Суринаме, Венесуэле, Боливии и Французской Гвиане. Внесен в Международную Красную Книгу, в Приложение II о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQMdYFyumjFPqJZ6bTTEsjMFI85i1tLXAyWsi-VcXxnb3rDD4Xop3DlGgHPVg&s', '2f3d00', 55.057637889362404, 82.88015416457901);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('1ca35c87-c0c4-491d-875d-b89602880358', 'Беличий саймири', 'Млекопитающие', 'Приматы', 'Беличий саймири', 'Распространен на севере Южной Америки, от Колумбии и Гайаны, до Северной Бразилии и северо-востока Перу. Внесен в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT4hEbt_8kjNlV8pCR8u0dPMZsfbGHu_PWfqy1GWHpEL_i9hlcahLlbh6-UGw&s', '2f3d00', 55.05448054825694, 82.878783269179);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('e853247d-72c1-4577-97b6-1fc7e962e8d6', 'Боливийский саймири	', 'Млекопитающие', 'Приматы', 'Боливийский саймири	', 'Обитают в Боливии, Бразилии и Перу в приречных лесах.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnJhG_lZKVnetyHczX25pCEXN-8JPJQNsgx2GptefYaoUw_RJv5atSO40L6js&s', '2f3d00', 55.05375052472679, 82.87616480299647);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('c8a4a54e-922b-4681-a30c-79cfb9953822', 'Зеленая мартышка', 'Млекопитающие', 'Приматы', 'Зеленая мартышка', 'Распространена на востоке Африки, в Восточном Судане, Эритреи, Эфиопии. Продолжительность жизни в природе до 20 лет, в неволе — до 45 лет. Внесена в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTyK1E7L3pnsGPukQfz6HLMhqxmo4DrHgEANceWrDMqrAztOaT6dAw0NBnLlks&s', '2f3d00', 55.05814665256201, 82.88058774878483);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('8a5217ff-2d50-4383-bb09-93bb03b78d5c', 'Мартышка Бразза', 'Млекопитающие', 'Приматы', 'Мартышка Бразза', 'Ведет древесный образ жизни. Держатся обычно небольшими семейными группами, но иногда собираются до 35 особей. Питается плодами деревьев, семенами, листьями, насекомыми. Беременность 180-213 дней. Рождается 1 детеныш.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRQ6d2DadNDyKE5GyWFEBpAJJUgy0I-zvVCJ97-6mQfbSsPbk8KasWqdnngtQ&s', '2f3d00', 55.05545323950731, 82.8832087399382);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('1b6c5445-e9a1-4130-92f8-e00fe47e4ff1', 'Восточный колобус', 'Млекопитающие', 'Приматы', 'Восточный колобус', 'Восточный колобус или гвереца был обнаружен немецким натуралистом Эдуардом Рюппелем во время поездки в Абиссинию между 1830 и 1834 годом. «Колобус» в переводе с греческого означает «искалеченный» это название он получил в связи с отсутствием большого пальца кисти. Еще одной отличительной особенностью является сложное строение желудка, напоминающее таковое у жвачных животных.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTFB9PyKtJ04FL1ouKJPLuXjCJqEEyfFG7JaPHZ1UtkkGx8K3C7u7RbaSi-Ag&s', '2f3d00', 55.057886491046624, 82.8837157486742);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('9f7b6566-ae69-41f3-87a9-7d434bdd5dfe', 'Мартышка Диана', 'Млекопитающие', 'Приматы', 'Мартышка Диана', 'Распространена в западной части Африки от юга Гвинеи и Сьерра Леона до юга Ганы. Внесена в Международную Красную книгу, в Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTJDqFPwW6st_j8Z8cHWouuKvrhSteYbcRhqNN04uajXVqLSJjt_Lf9DTEaVGc&s', '2f3d00', 55.05365801618923, 82.8774456583742);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('c1739715-86e0-4882-b0fc-e6d18ab58edb', 'Мандрил', 'Млекопитающие', 'Приматы', 'Мандрил', 'Мандрил или сфинкс распространен на западе Центральной Африки, на юго-западе Камеруна, Габоне, Юго-Западном Конго. Внесен в Международную Красную книгу, в Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQpAwsWZu8rL--qALSGhkMu9_8cYB5SKI5iYeexPL2IN_zhUW7mKjXGBrQTbg&s', '2f3d00', 55.05358397416776, 82.87747114646699);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('9bb76905-517a-4ffa-bb78-6875d19020a9', 'Яванский лангур', 'Млекопитающие', 'Приматы', 'Яванский лангур', 'Яванский лангур или Блестящий гульман распространен в Индонезии на островах Ява, Бали, Ломбок. Продолжительность жизни до 30 лет.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSQv_N4XZ66eSNyZ6iU9yYzeCZgiwvpaJh_yT3anlAsudXOqRjZ2xistN5yDQ&s', '2f3d00', 55.053829833293435, 82.8774006569953);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('8dda2ef7-8021-4c0c-9335-e65fc6987bce', 'Дымчатый очковый лангур', 'Млекопитающие', 'Приматы', 'Дымчатый очковый лангур', 'аспространён на Малаккском полуострове и на прилегающих островах, ареал простирается от южной Мьянмы и Таиланда до Малайзии. Средой обитания вида являются леса, при этом предпочтение отдаётся горным первичным и вторичным лесам.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRQJhw_bgEUeA49JfBU48xJCzaS65KQhY_XambbmEloGxMBndBDa7QCSLaf-g&s', '2f3d00', 55.05510727983155, 82.88324775609995);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('ac8c2bba-cc01-4d72-a5a2-4e017777709c', 'Камбоджийский гиббон', 'Млекопитающие', 'Приматы', 'Камбоджийский гиббон', 'Камбоджийский гиббон или Кампучийский гиббон.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSMLibiqTnY5R08EjkE8o-1w6F8F_gqaKtARGtm7WtDb7yrKEY2RW0aEv1ZxcY&s', '2f3d00', 55.054249997111654, 82.87919476105719);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('84ae6bc0-a869-4b97-af07-bd746e5699bd', 'Борнейский орангутан', 'Млекопитающие', 'Приматы', 'Борнейский орангутан', 'Борнейский орангутан или Калимантанский орангутан один из двух ныне существующих видов орангутанов; обитает как на индонезийской, так и на малайзийской территориях острова. В самом названии орангутанов подчёркивается их близость к человеку. В переводе с малайского языка слово «орангутан» означает «лесной человек». Орангутаны живут дольше иных гоминидов, не считая человека. В неволе известны случаи, когда особи жили дольше 60 лет. Внесен в Международную Красную книгу, Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSJMF_59zw--JWT52YRu7FMyvAPUbs_KYf-doCEYXZ1EWOChJitKQjRR4Va_F8&s', '2f3d00', 55.05459397388835, 82.87477733579841);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('3235fb44-ff28-454f-ae60-bb317a2bb345', 'Двупалый ленивец', 'Млекопитающие', 'Неполнозубые', 'Двупалый ленивец', 'Двупалый ленивец или унау один из двух сохранившихся видов двупалых ленивцев распространен в Южной Америке, Северной Бразилии, на востоке Перу и Эквадора, Суринаме, Гайане, южной Венесуэле и Колумбии. Внесен в Международную Красную книгу.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTbuQDqX6ss7SN0dgMmcdMkBYZDTMeRlEJdYCqECHsmFDY8SnD7XYoz6we9I5M&s', '2f3d00', 55.05374870180946, 82.88049818284127);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('60e01c89-ac0b-4124-97db-8101b0102af1', 'Заяц-беляк', 'Млекопитающие', 'Зайцеобразные', 'Заяц-беляк', 'битает в тундровой, лесной и частично лесостепной зоне Северной Европы (Скандинавия, северная Польша, изолированные популяции в Ирландии, Шотландии, Уэльсе), России, Казахстане, северо-западной Монголии, северо-восточного Китая, Японии (о. Хоккайдо). Акклиматизирован в Южной Америке (Чили и Аргентина). Населяет некоторые арктические острова (Новосибирские, Вайгач, Колгуев). В сравнительно недалеком прошлом был распространён значительно южнее; реликтовый участок прежнего ареала сохранился в Швейцарских Альпах.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSWB-k4srN412A9RU2d2XcohBPlppHQKWVYN7hykC8IiDxkreariQgudInvrZk&s', '2f3d00', 55.05603043471122, 82.8823007214993);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('67835073-3cb1-4e45-bd5d-2703a9552d83', 'Обыкновенная летяга', 'Млекопитающие', 'Грызуны', 'Обыкновенная летяга', 'Описание животного появится позже', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ6pmn5PnwZDkZDGbH7kbkKmVINdUdp0tmQsaFIvCuBidEGtBADWbAwZD_eGJc&s', '2f3d00', 55.058027837967735, 82.8773894949037);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('e2d8cade-8ac2-4986-9467-b11576b41a5d', 'Гигантская белка летяга', 'Млекопитающие', 'Грызуны', 'Гигантская белка летяга', 'Гигантские белки летяги — ночные животные, однако у наших посетителей есть все шансы их увидеть, по утрам эти белки довольно активны. В помёте рождается 1-2 детёныша. В рационе этих животных орехи, семена, ветки, фрукты.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTNtr3yhd4iV5aetX_kCsXgMXZDAJEIe6q2cx256Ct6EDQ74I2nDRbOaXQ-LTU&s', '2f3d00', 55.055007595278965, 82.87798683314794);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('e2f6a33c-bf1f-4cd3-9cff-6da1cb9dfccc', 'Гребенчатый дикобраз', 'Млекопитающие', 'Грызуны', 'Гребенчатый дикобраз', 'Гребенчатый дикобраз или Хохлатый дикобраз - крупный грызун; второй по величине в фауне Старого Света после бобра и третий среди грызунов вообще после бобра и ещё более крупной южноамериканской капибары.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRMXWLQgqq3Z8vR1Ca_NcBoQnnMPxOzSE87ldVzSKQPvPJ8ccsETOPxLDZWPjM&s', '2f3d00', 55.05804473425159, 82.87441916268449);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('8471e853-038c-4ef3-af09-5784a54e0a30', 'Патагонская мара', 'Млекопитающие', 'Грызуны', 'Патагонская мара', 'Патагонская мара или патагонский заяц обитает в Южной Америке. Продолжительность жизни до 14 лет.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSBuNA6vphqDzVbZ2c8n0dd0b6W4xz31XWsb-YCVWKm8xyFR44juapkJ6mTlg&s', '2f3d00', 55.05787955010979, 82.8855518653737);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('058fd839-0496-4f46-8668-d7bfc4fddbfa', 'Горная свинка Моко', 'Млекопитающие', 'Грызуны', 'Горная свинка Моко', 'битает на северо-востоке Бразилии в скалистых ландшафтах в штатах Минас-Жерайс, Баия и Пиауи.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTaSSKYwFUj1WlflO0_TKwNOv9Zey_P5P1l6oaqgGZ2WFWDHAhRbDFC7MYCqQ&s', '2f3d00', 55.053432176033375, 82.87884988834709);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('93d1b0a8-7344-4abe-8cec-c5fa036b0455', 'Оливковый агути', 'Млекопитающие', 'Грызуны', 'Оливковый агути', 'и Азары населяет восточную Южную Америку. Они живут на востоке и юго-востоке Бразилии, в Парагвае и в северной Аргентин', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ1b8sQcHoRNCC1gTe__b2f2OqOsKLB-gF4mY5qdC3e350zJ19aRRhc1PG7yw&s', '2f3d00', 55.056273317361345, 82.88166997171552);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('74ff266a-8236-4701-bd20-b9c9ec341cbb', 'Пака', 'Млекопитающие', 'Грызуны', 'Пака', 'меет грубый мех без подшерстки, от темно-коричневого до черного на верхней части тела и белый или желтоватый на подбрюшье; на шерсти обычно имеет от трех до пяти рядов белых пятен вдоль его сторон. У него толстые сильные ноги, с четырьмя пальцами на передних лапах и пять на задних лапах (первый и пятый уменьшены).', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSAvj_6thADaMy5lBjhN7UO3kn6dYVnoArSwJh_5nYrL6XWMZY8nIifBXINxfY&s', '2f3d00', 55.05496992591477, 82.8773376491223);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('e8f9194f-7e19-4261-b804-3f21d7897aaa', 'Капибара', 'Млекопитающие', 'Грызуны', 'Капибара', 'Капибара, или водосвинка - самый крупный среди современных грызунов. Распространена в Южной Америке в таких странах: Аргентина, Боливия, Бразилия, Венесуэла, Гайана, Колумбия, Парагвай, Перу, Уругвай, Французская Гвиана. Область распространения включает бассейны рек Ориноко, Амазонки и Ла-Платы. Внесена в Международную Красную книгу.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxHtTrl3pYroL51pJ8Y5YEC8SVPF0C__be4EDV7RvljH8gfLjowo3NS3n6aw&s', '2f3d00', 55.05773363767381, 82.87685433049867);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('f7193fa1-d641-4e6d-a377-63daefd5aa0d', 'Серебристая нутрия', 'Млекопитающие', 'Грызуны', 'Серебристая нутрия', 'Нутрия, или коипу, или болотный бобр — единственный вид семейства нутриевых. Естественный ареал нутрии ограничен южной половиной Южной Америки от Боливии и южной Бразилии до Огненной Земли. Она была акклиматизирована во многих странах Европы и Северной Америки; в Африке не прижилась. В СССР акклиматизация нутрии удалась в Закавказье, Киргизии и Таджикистане.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRhltnszugpq30GgD8PzskBhuCg0Jaa956K-nrP6apMeFmpL-uggopr1KfRmB8&s', '2f3d00', 55.05469000602977, 82.87935280331318);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('f0d882de-d1d0-424d-b182-b9b67faa8098', 'Дегу', 'Млекопитающие', 'Грызуны', 'Дегу', 'Белки дегу или чилийские белки – эти милые зверьки становятся популярными с каждым днём, и похоже совсем скоро они дадут фору декоративным кроликам, шиншиллам и хомячкам.  Дегу – социальные животные, поэтому желательно содержать их группой. Поодиночке они зачастую чувствуют себя неуверенно и требуют повышенного внимания хозяина.  Дегу может отбрасывать кончик своего хвоста, наподобие ящерицы. Эта удивительная способность часто помогает в естественных условиях обитания спасти зверьку жизнь. Место «отлома» хвоста довольно быстро подсыхает и заживает, не причиняя зверьку неудобств. Единственный минус — без хвоста дегу хуже держит равновесие. Поэтому не рекомендуется дергать животных за хвост.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSBSVb3w_2NNNkMbMbUxvV_zJXT-ZRn1sV2Hl5x8lZTNIJCOWOrfEbtmc0Efw&s', '2f3d00', 55.05659349158445, 82.87439314547494);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('0add5d13-01f3-4085-a392-7ce3db40b1cc', 'Волк лесной', 'Млекопитающие', 'Хищные', 'Волк лесной', 'Распространен в Евразии, Северной Америке. Внесен в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSdxdFenQuckUg4SEY9FAG7dSUD6bo95sze2MczlbPydugAlwN8nY9tLxfRTnY&s', '2f3d00', 55.05329707295281, 82.88112040616278);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('2b900a4f-0d37-4b4a-8936-847b43bff7e4', 'Волк полярный', 'Млекопитающие', 'Хищные', 'Волк полярный', 'Описание животного появится позже', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSqSsK8ppPyD31NPL8sCIdMhp0pHDHP95xuZJ9IjrrHBFrGK1zXM2P7P-T32Q&s', '2f3d00', 55.055987123465336, 82.87800717360874);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('6207064b-ad41-46a9-a086-c7c7d7291012', 'Черный волк', 'Млекопитающие', 'Хищные', 'Черный волк', 'Описание животного появится позже', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT3xxD8gr9aKl8KOG02JruaZ20g_Tla1QRhn8Q49RpeLlEaD6K2oCv-hv3CrQ&s', '2f3d00', 55.0562621227058, 82.87676070873331);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('8264c63a-0dcf-40e7-9d14-3d6cad9df376', 'Красный волк ', 'Млекопитающие', 'Хищные', 'Красный волк ', 'Распространен в Центральной и Южной Азии, о-вах Суматра и Ява, южных районах Дальнего Востока, Восточной Сибири. Внесен в Международную Красную книгу, Красную книгу РФ, в Приложение II Конвенции о международной торговле. Встречается крайне редко. На территории России в Приморском крае последний раз видели красного волка в 1972 году.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSdBG9L1iEeBI65-i4hC99XwHDK32Ym-MmfhPX770YkeXI1V_WZgjCY6MK4rBg&s', '2f3d00', 55.05589135338175, 82.88378109290727);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('e8d83490-d7d6-44ce-a45a-e78cecd37fca', 'Фенек', 'Млекопитающие', 'Хищные', 'Фенек', 'Фенек – самая маленькая африканская лисичка. В зоопарках мира этот вид представлен мало и в неволе размножается очень редко. Распространен в Северной Африке, на Синайском полуострове. Внесен в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTVfvRvMyRx-X8Ne48NDW6IUZER6mYhVrl2RQMPIWgjnILYbVyxMIFP5hx8Xw&s', '2f3d00', 55.054446003651336, 82.88215978856226);
