INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('381dcfc0-77ee-4fa3-88d8-a31d77c20577', 'Бурый медведь', 'Млекопитающие', 'Хищные', 'Бурый медведь', 'Бурый медведь – второй по массивности хищник на планете. Распространен в Европе, Азии и Северной Америке от северной до южной границы лесов. Территория обитания огромна, но во многих районах он истреблен. В Альпах Австрии остались единицы, в горах Испании и Италии несколько десятков, в США сохранился только в пяти штатах. И лишь на Аляске, Камчатке и в Сибири численность еще довольна высока.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQColESiVcJa1pxI9xlE5K-HloxHDFGls9GqqVovBVJctABrBFgE5a1GdbYVg&s', '2f3d00', 55.05384596781318, 82.87533254777311);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('48fd50c8-de32-4d8c-8a63-af2c4973f687', 'Уссурийский белогрудый медведь', 'Млекопитающие', 'Хищные', 'Уссурийский белогрудый медведь', ' спячку залегает в дуплах деревьев, в пещерах и расщелинах скал. Питается растительной пищей (желуди, кедровые орехи, трава, ягоды, плоды), насекомыми, дикими копытными. Беременность 180-240 дней. Рождается 1-3 детёныша.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQzpbZRj0OQeyb4i6H_OluvDgoGy_KJ8MwUgBjOFgZ7q1Opp6JuWxcVQrmDIA&s', '2f3d00', 55.05610877462671, 82.88030577739299);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('4074065e-4f83-400f-9dc9-d5786343ebc6', 'Белый медведь', 'Млекопитающие', 'Хищные', 'Белый медведь', 'Белый медведь - самый крупный из ныне живущих наземных хищников, единственный представитель существующих млекопитающих, весь жизненный цикл которых проходит на дрейфующих льдинах Арктики. Распространен на островах и побережьях Северного Ледовитого океана. Внесен в Красную книгу МСОП, Красную книгу РФ, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQZBk_clTP7YI-05r7-U0-q9kTaBxPpYYt5wBoqiwg-krNvbaJ93AvXowOUJqE&s', '2f3d00', 55.05453679624505, 82.87513446044404);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('5bdc917d-9b7e-4e64-850c-607ed434c132', 'Носуха ', 'Млекопитающие', 'Хищные', 'Носуха ', 'Распространена в Южной Америке от Колумбии и Венесуэлы до Северной Аргентины. Внесена в Международную Красную книгу, в Приложение III Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR92iPcJ45hv_PF9pTICGCyp7OxX4Di_9E2jFcWg632cAn1tEGDrGs12jKgdDI&s', '2f3d00', 55.055681698175015, 82.87366105766263);
