INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('a487fa3b-c3d1-409b-b9ad-9a1f0c416665', 'Кинкажу', 'Млекопитающие', 'Хищные', 'Кинкажу', 'Распространен в Южной Америке, от Мексики до Боливии. Внесен в Международную Красную книгу, в Приложение III Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTc3JHWFhy9LkrnXVyct2OCE_AYt0iPfUnAgI4AmCcIRrEctZZ4tXDWrMFjnw&s', '2f3d00', 55.05718343853942, 82.88493519997435);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('247b58c1-9484-4e6a-b57d-dff5a436056e', 'Росомаха', 'Млекопитающие', 'Хищные', 'Росомаха', 'Распространена на территории Скандинавских стран, Эстонии, России, на севере Монголии и Китая, Канаде, США. Внесена в Международную Красную книгу.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTWrlgxzouWdnjibjimTO6yLMlS6EUQG5PCRo_GHm5inXCCTVmZww9c73fMdkQ&s', '2f3d00', 55.05377434782648, 82.8809998859256);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('42e22a80-8efc-4e4b-9ae3-0079810bd08b', 'Речная выдра', 'Млекопитающие', 'Хищные', 'Речная выдра', 'Распространена в Европе, Азии (кроме Аравийского полуострова и крайнего Севера), Северной Африке.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSTwSGiMQiotGFkIHo9ZezC_MZBKEn5PtSgF_kt6rdos2kFzE5pI1I6mLe8iyM&s', '2f3d00', 55.05726704214149, 82.88644989639639);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('82203d9e-1ac3-4b07-9d7f-3f41083601c4', 'Уссурийская харза', 'Млекопитающие', 'Хищные', 'Уссурийская харза', 'Харза называется также пестрой, желтогрудой или дальневосточной куницей. Обитающая на Дальнем Востоке харза – самая крупная и наиболее яркая по расцветке меха среди сородичей, обитающих в Азии. Распространена в Амурском и Уссурийском краях, на северо-востоке Китая, Корейском полуострове. Внесена в Международную Красную книгу, в Красную книгу РФ, Приложение III Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRPk-QrYoNRwUjwIZSsD9B3uAGZEXAUcY1MaR4YY0H7R6jAGZCNKStrtMFAjQ&s', '2f3d00', 55.05400087288977, 82.88173415478708);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('082f51c1-fcc9-4af5-904d-ca871be0d88a', 'Тайра', 'Млекопитающие', 'Хищные', 'Тайра', 'едет одиночный образ жизни. Активна в любое время суток. Наибольшую активность проявляет на рассвете и в сумерках. Хорошо лазает по деревьям, в случае необходимости может совершать большие прыжки. Питается фруктами, мелкими позвоночными, падалью. Беременность 63-67 дней. Рождается 1-3 детеныша.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQVG-KbjE3m6EjX3Dzf_LCB_GDI7Ht84RxAApjASO5Wq0afBKZPtnBthwD_og&s', '2f3d00', 55.05811234245472, 82.8765655863787);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('1abc367c-b7d6-472b-824e-41904808e9e2', 'Соболь', 'Млекопитающие', 'Хищные', 'Соболь', 'Соболь – ценный пушной вид, обитает только на территории России, экспорт которого запрещен. В настоящее время сохранился только в Сибири и на Дальнем Востоке. Издавна на Руси соболиный мех выступал в виде денежного средства, служил символом достатка. Соболь действительно был «меховым золотом» России. Его добывали везде, где он встречался, и к началу ХХ века численность зверей в России катастрофически сократилась. Живущий в основном в лесах таежной зоны, соболь исчез из европейской части России, да и в Сибири огромные пространства остались без соболей. Полный запрет отстрела зверей в 1912 году, организация Баргузинского заповедника, охрана и расселение соболей на прежние места обитания помогли спасти вид от полного истребления. Внесен в Международную Красную книгу.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT6VJ4xYxrUCiH7vHRdpBvmZf6cdSAFGMol9ExbHwn63C1gOEembwpBwHJDagE&s', '2f3d00', 55.05523108260592, 82.88317005958159);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('658c72f4-61c9-47c4-b833-752eabc7e79c', 'Куница каменная', 'Млекопитающие', 'Хищные', 'Куница каменная', 'Описание животного появится позже', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTy_6Tm0LzunVM-17qKEl8_lp2slyxy4bB3rTx81KVr5Qf9PgMKTbmeCv37H6E&s', '2f3d00', 55.05590423582069, 82.88330710883419);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('6492723e-f759-4487-ac83-a69c29ca4a9f', 'Куница лесная', 'Млекопитающие', 'Хищные', 'Куница лесная', 'Распространена в Европе, Западной Сибири, на Кавказе, в Турции, Северном Ираке и Иране. Внесена в Международную Красную книгу.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQR4TzWZEBD7xMxfb4FFJhsSyd8lzjVeMfSgOgvzbrk8CNYHBHGN445DOA0Cw&s', '2f3d00', 55.0552024895137, 82.88054369067852);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('c43f301a-ef0b-4242-9795-21cf615965ac', 'Обыкновенный барсук', 'Млекопитающие', 'Хищные', 'Обыкновенный барсук', 'ивет небольшими семейными группами. В северных районах зимой впадает в спячку. Активен в сумерках и ночью. Питается в основном грызунами, земноводными, пресмыкающимися, моллюсками, орехами, червями, ягодами и насекомыми. Беременность около 280 дней. Рождается 2-6 детенышей.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxaUqKDvkmEJ4WUJvVX5j-QThV2L_V9QKluiQgGWzYKhJ8SyHRryWD8OrUIA&s', '2f3d00', 55.0575603009715, 82.88369183996824);
