INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('6883baa0-4455-473d-a2b4-a69675ac5a3c', 'Перевязка', 'Млекопитающие', 'Хищные', 'Перевязка', ' качестве убежищ использует норы грызунов. Питается грызунами, птицами и их яйцами, ящерицами и иногда плодами, ягодами. Беременность вместе с латентным периодом 8-9 месяцев. Рождается 3-12 детёнышей.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS6uJDOsYv4XA-moMpEhUzjyY4gXq1j6DRGRb_fyosbZKowD_rAS06sZzyu2-0&s', '2f3d00', 55.05446867540715, 82.87777764925364);
