INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('e2044c76-afc0-43cc-8c19-3a319155937a', 'Норка американская', 'Млекопитающие', 'Хищные', 'Норка американская', 'Распространена в Северной Америке, акклиматизирована в Европе, России, Китае, Японии. В связи с расселением американской норки на территории России ареал европейской норки значительно сократился, а в некоторых районах европейская норка исчезла. Внесена в Международную Красную книгу.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRep6ox5ZHKIG-0mj-C3CYYg9aQMMeoaXzR-XEuGtweS0eZG-A_xnqP0qVLUUY&s', '2f3d00', 55.05706461650663, 82.88031536042502);
