INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('4ab41fd4-0238-4d38-8d81-04253a404345', 'Каракал', 'Млекопитающие', 'Хищные', 'Каракал', 'ля убежищ использует норы лисиц, дикобразов. Питается грызунами, зайцами, мелкими антилопами, птицами, пресмыкающимися, насекомыми. Беременность 68-75 дней. Рождается 2-4 детёныша.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTG9iaGImndq08SUHt2g-Wa-noRQW2ODfQrP4GK0ItZbe2xL3pushu-ys8Fhw&s', '2f3d00', 55.056663756279896, 82.87814452417226);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('b9a49f94-8e66-45b2-99ab-dda68e654a94', 'Камышовый кот', 'Млекопитающие', 'Хищные', 'Камышовый кот', 'итается птицами, мелкими грызунами, рыбой, насекомыми, часто охотится на нутрий и водоплавающую птицу. Хорошо плавает. Беременность около 66 дней. Рождается 2-5 детенышей.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQlF0J1y6fRWhqpLJGOKEIJj_23NNrXCvxhNJLvBBD2H8yk9BI_afS7UFRBvgo&s', '2f3d00', 55.05767295883878, 82.88322838282059);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('227ae557-3a11-4b3d-86bb-9ad3c9e90ee1', 'Манул', 'Млекопитающие', 'Хищные', 'Манул', 'Сибирский манул - самый северный подвид, который приспособился к резко континентальному климату с очень низкими температурами воздуха зимой (до -50°С) и снежным покровом незначительной величины. Осенью и в начале зимы манул бывает чрезвычайно жирен и малоподвижен. Высокий, рыхлый снежный покров затрудняет передвижение и добывание пищи, что не позволяет существовать этим кошкам в равнинных и горных лесах, где снега особенно много. Излюбленным местом обитания являются нагорья, холмистые пустыни, полупустыни и степи с выходами камней, расположенные, как правило, на склонах невысоких гор (до 1100-1500 м).', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTBIWZYBEWAFkbhSBxGEd39vIJTmExRk2mojlxLFQqy8-MttRXZP6vVhXGhzQ&s', '2f3d00', 55.05512507995927, 82.88176428160723);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('de770e73-aa11-4b41-8b81-ad27a9ee17c9', 'Дальневосточный кот', 'Млекопитающие', 'Хищные', 'Дальневосточный кот', 'Распространен от южной части Дальнего Востока до Северо-Восточного Китая, на Корейском п-ове. Внесен в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQqc7bTH_xk0sZ6DGULC0fmVN68zb_UOZDIIkA1vfkaBD4onj34k5IOZUtb4w&s', '2f3d00', 55.05455482172371, 82.88013974165881);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('4cf23bb2-893b-4d7a-9772-0c193b83ab5f', 'Сибирская рысь', 'Млекопитающие', 'Хищные', 'Сибирская рысь', 'Рысь – самый крупный и самый северный представитель мелких кошачьих. Распространена в Западной Сибири, на Алтае, в Хакасии. Обитает в густых хвойных и смешанных лесах, горной тайге Алтая. Внесена в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR3gNCLjETQmObSBujWAXkK5-hACYZFVg-vJcCTOumI9Aa5UvjxhEczU6cfeg&s', '2f3d00', 55.05577007450786, 82.88149884282058);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('bcd9ee23-2609-4a98-bf07-9c4da1467fa4', 'Восточно-сибирская рысь', 'Млекопитающие', 'Хищные', 'Восточно-сибирская рысь', 'Описание животного появится позже', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ-QX3ndJFbMqOzM1xjN11HSSxPxknLj9tD8BoZFLGbbjDDO_6dZUr1x_wJypM&s', '2f3d00', 55.055878588595725, 82.88450861587147);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('24830c33-e89c-4d5a-9118-621f9c5b453a', 'Барханный кот', 'Млекопитающие', 'Хищные', 'Барханный кот', 'ктивен ночью и в сумерках. От высоких и низких температур укрывается в норах. Питается в основном мелкими грызунами, а также птицами, ящерицами, змеями, насекомыми и пауками. Одиночное животное. Беременность около 60-67 дней. Рождается 2-5 детенышей.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTSptz7iVWMt2tN9j1j9rI8lkj9rUWmrQUJ7QLMP2ZCUPpabNefHALR2TUc_w&s', '2f3d00', 55.05370639635395, 82.88141265350222);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('25d13490-b440-4589-bf8c-2dfc5ad86514', 'Оцелот', 'Млекопитающие', 'Хищные', 'Оцелот', 'Оцелот – малочисленный вид, который в ближайшее время может оказаться под угрозой исчезновения. Ставшие знаменитыми, из-за красоты своего меха, оцелоты были почти полностью уничтожены. Характер среды обитания и ночной образ жизни не позволяет вести их точный учет и изучать биологию. Распространен от южных границ США до Перу и Северной Аргентины.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQD4TBWc2VWA5Xy6udzvMYubpjcb9N2wm9mWtft52LcHqKfgd_sX_ieykkVm2Q&s', '2f3d00', 55.05503268649866, 82.88380079085016);
