INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('fa236a02-86d7-4d1f-9e90-7f1ed1d30097', 'Степной кот', 'Млекопитающие', 'Хищные', 'Степной кот', 'Распространен в Центральной Азии, Индии. Внесен в Международную Красную книгу, в Приложение II Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS1xgFI4INfOriqihwiWNAenDMerpXqCsOuX_Ni2SDwYAf8vMFrZtNJ9Kk3zQ&s', '2f3d00', 55.055697611821046, 82.87498858421434);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('27b4a2a0-8920-4f1c-a84a-71d82a747c25', 'Виверровая кошка', 'Млекопитающие', 'Хищные', 'Виверровая кошка', 'ктивна ночью и в сумерках. Виверровая кошка или кошка – рыболов, единственный представитель семейства кошачьих, который хорошо плавает и ныряет. Питается рыбой, ракообразными, а также птицами и млекопитающими. Беременность 57-67 дней. Рождается 2-3 детеныша.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSzh_I4zBcSFp7rjRMBL0PbZBFfXkeqLqsb9K_fpVEOeaqitZT_rhCIZAF6nlo&s', '2f3d00', 55.0573497644872, 82.87789929466814);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('0748ada8-e055-439b-9abf-bb7620ad99a8', 'Ягуарунди', 'Млекопитающие', 'Хищные', 'Ягуарунди', 'едет наземный образ жизни, но и хорошо лазает по деревьям. Активна обычно ночью. Питается мелкими млекопитающими, птицами, рептилиями. Одиночное животное. Беременность 63-70 дней. Рождается 2-4 детеныша.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSkSEvY0t5P2fkS0EGF6FU_dLoEpBB4j-alr0Sa8kkNovui9tk02RMzLezsfA&s', '2f3d00', 55.05469007383499, 82.88132680098663);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('d2a906a9-055e-4c74-b216-7973dd99721d', 'Дымчатый леопард', 'Млекопитающие', 'Хищные', 'Дымчатый леопард', 'Описание животного появится позже', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQfjcCAcS4e6RdWRadCfgRZ4_8g981MCqFZe9OrUBxqJxF2S4gf74Gp5HnZ9g&s', '2f3d00', 55.0580207480475, 82.87980074399678);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('50156360-0c1c-4b22-8064-ecb469a36ab1', 'Африканский лев', 'Млекопитающие', 'Хищные', 'Африканский лев', 'оловой диморфизм очень резко выражен, густая и длинная грива самцов придает им весьма своеобразный внешний вид.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRP0UxXF9yw0F3XtbBGcOsi8gUyIYIaycB2Gh-8DVlanWauz5pH6nWWRN0asQ&s', '2f3d00', 55.055748555979406, 82.87904654297887);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('40367515-c868-422a-9787-8b09c3c22bf8', 'Дальневосточный леопард', 'Млекопитающие', 'Хищные', 'Дальневосточный леопард', 'Питается копытными, зайцами и птицами. Свою добычу часто затаскивает на дерево. Ведет одиночный образ жизни. Беременность 90-100 дней. Рождается 1-4 детеныша.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTw4kQwSTxgSYFmD_XG3k4DguVYYv8n8fi3kT_a-DIRn6sPmpWfWHwFBidwrw&s', '2f3d00', 55.05628089110044, 82.87839850775659);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('15c9d2c2-93b6-4647-ba05-8dbac4b2e94b', 'Персидский леопард ', 'Млекопитающие', 'Хищные', 'Персидский леопард ', 'хотится, в основном, в сумерках и ночью. Питается копытными, грызунами, птицами, пресмыкающимися. Беременность 90-105 дней. Рождается 1-4 детеныша.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTQ7ZJmO7Gd6acnmKBcGXbp3ttTuHKV9syyiiygz_CKP5aZCOiS__t1TgQ5GEY&s', '2f3d00', 55.05640272054885, 82.87426511957806);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('4120be1c-c244-41d5-8a48-e8890af3fcdb', 'Кавказский леопард', 'Млекопитающие', 'Хищные', 'Кавказский леопард', 'Распространен на Большом Кавказе, в Закавказье. В природе практически исчез. Внесен в Международную Красную книгу, Красную книгу РФ, в Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSj7L4P3BP9hwd97Z1dDBU2Kh9dJKFs2zP-o1REdzTGeplLMBRCNiICdFJe-8k&s', '2f3d00', 55.0578632384891, 82.88123928982762);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('9adc9152-d078-45d0-a315-dd6b70a74d99', 'Амурский тигр ', 'Млекопитающие', 'Хищные', 'Амурский тигр ', 'Распространен на Дальнем Востоке, в Уссурийском и Приморском краях. В природе, благодаря природоохранным мероприятиям, численность возросла с 220 до 350 особей. В настоящее время в 289 зоопарках мира содержится 937 амурских тигров. Внесен в Международную Красную книгу, Красную книгу РФ, в Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSBv0fVyusqk3dKTAnsGq6v0R6behmPCV4d49UBSvGka4FTErpE1cYRycsVLDw&s', '2f3d00', 55.05504571496462, 82.8764697047394);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('15b1e4ee-6cf7-4017-a976-5b8777cb9507', 'Бенгальский тигр белая вариация', 'Млекопитающие', 'Хищные', 'Бенгальский тигр белая вариация', 'ерсть светло-кремового цвета с серо-коричневыми полосами. Глаза голубые с розовым зрачком. Это – результат нарушения окраски кожных покровов и радужной оболочки глаз, обусловленный генетическими механизмами.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRHV_RGyrmdUcjrYHI6WrOZZCTZwgq77jajtuZm-LW8gjcHkPPb-5f8GIeroXU&s', '2f3d00', 55.05587521526649, 82.878812309797);
INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('acd0f0dd-c391-4768-a42c-3b55fe151401', 'Гепард', 'Млекопитающие', 'Хищные', 'Гепард', 'Распространен в Африке и Азии. Внесён в Международную Красную книгу, в Приложение I Конвенции о международной торговле. Гепард отличается мирным нравом, сравнительно легко приручается и сильно привязывается к человеку. С давних времен использовался в качестве ловчего зверя. В Египте еще недавно выполнял роль сторожевой собаки. Первые сведения о приручении и использовании гепарда на охоте в Египте и Азии (Палестина, Иордания, Сирия, Ирак, Афганистан, Пакистан, Индия, Китай) датируются вторым тысячелетием до н.э. С Х-ХI века охота с гепардом была очень популярна на юге Европы, в средние века в Древней Руси также практиковалась охота с гепардами.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTo817dWEUCBaMrg7jDfFaOWr1LwyPCVWxagYkLBNS8NvIYvKDdbSQE9t4dIw&s', '2f3d00', 55.05799527112123, 82.88334739425386);
