INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('8eeaa4da-502a-4017-a4ae-8b1cb879f1ea', 'Двугорбый верблюд', 'Млекопитающие', 'Парнокопытные', 'Двугорбый верблюд', 'актриан (двугорбый верблюд) лучше дромадера (одногорбного верблюда) переносит сильные холода. Его нередко можно видеть отдыхающим в снегу после дневного перехода. Ледяные ветры Центральной Азии не причиняют ему никакого вреда. В отличие от дромадера, шкура бактриана очень толстая. Верблюжий горб – склад жирового запаса, его вес может достигать 200 кг. Спина верблюда, который стоит на солнце, может быть нагрета снаружи до 80 градусов, а под шерстью температура будет 40 градусов. Он почти не потеет благодаря своей шерсти. Относятся верблюды к мозоленогим. Нижняя поверхность ступни расширена и представляет собой как бы подушку - это помогает верблюду не проваливаться при ходьбе по песку или по снегу. От песчаных и снежных бурь глаза верблюда защищены густыми ресницами. Верблюд может даже закрывать ноздри, чтобы песок или снег не попал в нос.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQgqYbXWamgp2ap3OEY7D7SSf2cjtk6YUf6Fyo1DAOe3Wv66TPQNzv6ZmjwALs&s', '2f3d00', 55.05529222697609, 82.87530195627373);
