INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('d51d26dd-87cb-4d95-aaf0-bbf5d033368d', 'Таджикский винторогий козел', 'Млекопитающие', 'Парнокопытные', 'Таджикский винторогий козел', 'Винторогий козел, или «мархур» – редкий, исчезающий вид, численность которого по всему ареалу находится в угрожающем состоянии и опасность исчезновения вполне реальна. Основная причина снижения численности – неконтролируемая охота и нарушение мест обитания. Распространен в горах Западного Тянь-Шаня, Западного Памира на юго-востоке Узбекистана и в Таджикистане. Внесен в Международную Красную книгу, в Приложение I Конвенции о международной торговле.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQXxRM43oFWAbg1pyl615rXbTRjowzqpObmSpAUTXIKK9Z238hKbvPNzIt2vg&s', '2f3d00', 55.057019770478604, 82.87686821108366);
