INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('d31a2de2-808d-4cbc-bfd5-8408baf2fb19', 'Овцебык', 'Млекопитающие', 'Парнокопытные', 'Овцебык', 'Овцебык - современник мамонта. В ледниковый период он был распространен по всей Европе, Америке и Сибири. В XX веке завезен на Аляску (где ранее вымер), в Евразию, на полуостров Таймыр, остров Врангеля, Норвегию. Распространен в Северной Америке: в арктических областях Гренландии и Канады. Внесен в Международную Красную книгу.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQC_SKkWEvVjgt0PpTJxXFop4j_V7vkQXVoA0DygRaNXg43yxv-HMA5fPsFAQ&s', '2f3d00', 55.055377807951174, 82.87442785383784);
