INSERT INTO animals (id, name, animalclass, family, species, description, imgpath, background, latitude, longitude) VALUES ('566ae91f-5939-4b46-b618-f36472548648', 'Азиатская дикуша', 'Птицы', 'Курообразные', 'Азиатская дикуша', 'Внесена в Красную книгу МСОП, Красную книгу РФ. Из трех видов дикуш, обитающих на нашей планете, единственный представитель - азиатская дикуша - является эндемиком России. Особенность дикуши - не бояться человека, транспорта - оказалась губительной для ее численности в последние десятилетия. Одной из действенных мер по сохранению этого уникального вида является искусственное разведение.', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSShogL_hTSb8i9EkVWLdT-CNB3vpI1kUxEac-nVLtEtAInZnC3Hq38Rv-UzWo&s', '2f3d00', 55.056860864770876, 82.88224988443558);
